      )
    })

    it('should maintain property test performance characteristics', () => {
      fc.assert(
        fc.property(
          fc.record({
            componentName: fc
              .string({ minLength: 3, maxLength: 15 })
              .filter((s: string) => /^[A-Za-z]/.test(s)),
