                  {analytics.distinguishedClubs.distinguished} Distinguished
                </span>
              )}
            </div>
          </div>

          {/* Projected Distinguished */}
          <div className="bg-gradient-to-br from-amber-50 to-amber-100 rounded-lg p-4 border border-amber-200">
            <div className="flex items-center justify-between">
              <div>
                <div className="flex items-center gap-1">
                  <p className="text-sm font-medium text-amber-700">
                    Projected Year-End
                  </p>
                  <Tooltip content="Estimated number of distinguished clubs by end of program year based on current trends">
                    <InfoIcon />
                  </Tooltip>
                </div>
                <p className="text-3xl font-bold text-amber-900 mt-1">
                  {analytics.distinguishedProjection}
                </p>
              </div>
              <div className="bg-amber-200 rounded-full p-3">
                <svg
                  className="w-6 h-6 text-amber-700"
                  fill="none"
                  stroke="currentColor"
                  viewBox="0 0 24 24"
                >
                  <path
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    strokeWidth={2}
                    d="M13 7h8m0 0v8m0-8l-8 8-4-4-6 6"
                  />
                </svg>
              </div>
            </div>
            <div className="mt-2">
              <span className="text-xs text-amber-700 bg-amber-100 px-2 py-1 rounded">
                Distinguished Clubs
              </span>
            </div>
          </div>
        </div>
      )}
    </div>
  )
}

import React, { useEffect } from 'react'
import { useDistricts } from '../hooks/useDistricts'
import type { District } from '../types/districts'

interface DistrictSelectorProps {
  selectedDistrictId: string | null
  onDistrictSelect: (districtId: string) => void
}

const DistrictSelector: React.FC<DistrictSelectorProps> = ({
  selectedDistrictId,
  onDistrictSelect,
}) => {
  const { data, isLoading, isError, error } = useDistricts()

  // Load persisted district from localStorage on mount
  useEffect(() => {
    if (!selectedDistrictId) {
      const savedDistrictId = localStorage.getItem('selectedDistrictId')
      if (savedDistrictId && data?.districts) {
        // Verify the saved district still exists
        const districtExists = data.districts.some(
          d => d.id === savedDistrictId
        )
        if (districtExists) {
          onDistrictSelect(savedDistrictId)
        }
      }
    }
  }, [data, selectedDistrictId, onDistrictSelect])

  const handleDistrictChange = (
    event: React.ChangeEvent<HTMLSelectElement>
  ) => {
    const districtId = event.target.value
    if (districtId) {
      onDistrictSelect(districtId)
      // Persist selection to localStorage
      localStorage.setItem('selectedDistrictId', districtId)
    }
  }

  const getSelectedDistrictName = (): string => {
    if (!selectedDistrictId || !data?.districts) {
      return ''
    }
    const district = data.districts.find(d => d.id === selectedDistrictId)
    return district?.name || ''
  }

  if (isLoading) {
    return (
      <div
        className="flex items-center space-x-2"
        role="status"
        aria-live="polite"
      >
        <div
          className="animate-spin rounded-full h-5 w-5 border-b-2 border-tm-loyal-blue"
          aria-hidden="true"
        ></div>
        <span className="text-gray-600">Loading districts...</span>
      </div>
    )
  }

  if (isError) {
    return (
      <div
        className="bg-red-50 border border-red-200 rounded-md p-3"
        role="alert"
        aria-live="assertive"
      >
        <p className="text-red-800 text-sm">
          Failed to load districts: {error?.message || 'Unknown error'}
        </p>
      </div>
    )
  }

  if (!data?.districts || data.districts.length === 0) {
    return (
      <div
        className="bg-yellow-50 border border-yellow-200 rounded-md p-3"
        role="status"
      >
        <p className="text-yellow-800 text-sm">No districts available</p>
      </div>
    )
  }

  return (
    <nav className="tm-nav space-y-4" aria-label="District selection">
      <div className="flex flex-col sm:flex-row sm:items-center gap-3">
        <label
          htmlFor="district-select"
          className="text-base font-semibold text-gray-900 flex-shrink-0"
        >
          Select District:
        </label>
        <div className="relative flex-1 sm:max-w-md">
          <select
            id="district-select"
            value={selectedDistrictId || ''}
            onChange={handleDistrictChange}
            className="block w-full min-h-[48px] px-4 py-3 pr-10 bg-white border-2 border-gray-300 rounded-lg shadow-sm hover:border-tm-loyal-blue focus:outline-none focus:ring-2 focus:ring-tm-loyal-blue focus:border-tm-loyal-blue text-base font-medium text-gray-900 transition-colors cursor-pointer appearance-none"
            style={{
              backgroundImage: `url("data:image/svg+xml,%3csvg xmlns='http://www.w3.org/2000/svg' fill='none' viewBox='0 0 20 20'%3e%3cpath stroke='%236b7280' stroke-linecap='round' stroke-linejoin='round' stroke-width='1.5' d='M6 8l4 4 4-4'/%3e%3c/svg%3e")`,
              backgroundPosition: 'right 0.5rem center',
              backgroundRepeat: 'no-repeat',
              backgroundSize: '1.5em 1.5em',
            }}
            aria-label="Select a Toastmasters district to view statistics"
            aria-required="true"
            aria-describedby={
              selectedDistrictId ? 'current-district' : undefined
            }
          >
            <option value="" className="text-gray-500">
              Choose a district...
            </option>
            {data.districts.map((district: District) => (
              <option
                key={district.id}
                value={district.id}
                className="text-gray-900"
              >
                {district.name}
              </option>
            ))}
          </select>
        </div>
      </div>

      {selectedDistrictId && (
        <div className="bg-blue-50 border-l-4 border-tm-loyal-blue rounded-r-lg p-4">
          <p
            id="current-district"
            className="text-sm sm:text-base text-gray-700"
            role="status"
            aria-live="polite"
          >
            <span className="font-medium text-gray-900">Viewing:</span>{' '}
            <span className="font-bold text-tm-loyal-blue">
              {getSelectedDistrictName()}
            </span>
          </p>
        </div>
      )}
    </nav>
  )
}

export default DistrictSelector

import React, { useState } from 'react'
import {
  BarChart,
  Bar,
  LineChart,
  Line,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  ResponsiveContainer,
  Legend,
  Cell,
} from 'recharts'
import { useEducationalAwards } from '../hooks/useEducationalAwards'
import { useDistrictStatistics } from '../hooks/useMembershipData'
import { ExportButton } from './ExportButton'
import { exportEducationalAwards } from '../utils/csvExport'
import { ChartContainer } from './ChartLegend'
import { ChartTooltip, DateTooltip } from './ChartTooltip'
import {
  getChartColorPalette,
  generateChartDescription,
  CHART_STYLES,
} from '../utils/chartAccessibility'

interface EducationalAwardsChartProps {
  districtId: string
  districtName: string
  months?: number
}

type ChartView = 'byType' | 'byMonth' | 'topClubs'

const EducationalAwardsChart: React.FC<EducationalAwardsChartProps> = ({
  districtId,
  districtName,
  months = 12,
}) => {
  const [chartView, setChartView] = useState<ChartView>('byType')
  const { data, isLoading, isError, error } = useEducationalAwards(
    districtId,
    months
  )
  const { data: statistics } = useDistrictStatistics(districtId)

  const handleExport = () => {
    if (data) {
      exportEducationalAwards(data, districtId, districtName)
    }
  }

  if (isLoading) {
    return (
      <section
        className="bg-white rounded-lg shadow-md p-6"
        aria-busy="true"
        aria-label="Loading educational awards"
      >
        <h2 className="text-xl font-semibold text-gray-900 mb-4">
          Educational Awards
        </h2>
        <div className="flex items-center justify-center h-80">
          <div className="animate-pulse flex flex-col items-center">
            <div className="h-4 w-32 bg-gray-300 rounded mb-2"></div>
            <div className="h-4 w-24 bg-gray-300 rounded"></div>
          </div>
        </div>
      </section>
    )
  }

  if (isError) {
    return (
      <section
        className="bg-white rounded-lg shadow-md p-6"
        role="alert"
        aria-label="Educational awards error"
      >
        <h2 className="text-xl font-semibold text-gray-900 mb-4">
          Educational Awards
        </h2>
        <div className="flex items-center justify-center h-80">
          <div className="text-center">
            <p className="text-red-600 font-medium mb-2">
              Failed to load educational awards data
            </p>
            <p className="text-gray-600 text-sm">
              {error?.message || 'An unexpected error occurred'}
            </p>
          </div>
        </div>
      </section>
    )
  }

  if (!data) {
    return (
      <section
        className="bg-white rounded-lg shadow-md p-6"
        role="status"
        aria-label="Educational awards"
      >
        <h2 className="text-xl font-semibold text-gray-900 mb-4">
          Educational Awards
        </h2>
        <div className="flex items-center justify-center h-80">
          <p className="text-gray-600">No educational awards data available</p>
        </div>
      </section>
    )
  }

  // Calculate average awards per member
  const totalMembers = statistics?.membership.total || 0
  const averageAwardsPerMember =
    totalMembers > 0 ? (data.totalAwards / totalMembers).toFixed(2) : '0.00'

  // Color palette for charts
  const colors = getChartColorPalette(8)

  // Format data for by-type chart
  const byTypeData = data.byType.map((item, index) => ({
    ...item,
    color: colors[index % colors.length],
  }))

  // Format data for monthly chart
  const byMonthData = data.byMonth.map(point => ({
    month: new Date(point.month).toLocaleDateString('en-US', {
      month: 'short',
      year: 'numeric',
    }),
    awards: point.count,
    fullDate: point.month,
  }))

  // Format data for top clubs
  const topClubsData = data.topClubs.slice(0, 10).map((club, index) => ({
    ...club,
    color: colors[index % colors.length],
  }))

  // Generate chart descriptions for accessibility
  const byTypeDescription = generateChartDescription(
    'bar',
    byTypeData.length,
    'Educational Awards by Type',
    `Shows ${data.totalAwards} total awards distributed across ${byTypeData.length} award types`
  )

  const byMonthDescription = generateChartDescription(
    'line',
    byMonthData.length,
    'Educational Awards Over Time',
    `Shows award trends over ${months} months with ${data.totalAwards} total awards`
  )

  const topClubsDescription = generateChartDescription(
    'bar',
    topClubsData.length,
    'Top Performing Clubs',
    `Shows top ${topClubsData.length} clubs ranked by educational awards earned`
  )

  return (
    <ChartContainer
      title="Educational Awards"
      subtitle={`Total Awards: ${data.totalAwards.toLocaleString()} | Avg per Member: ${averageAwardsPerMember}`}
      isLoading={isLoading}
      {...(isError
        ? {
            error: error ? String(error) : 'An unexpected error occurred',
          }
        : {})}
      className="tm-brand-compliant"
    >
      <div className="flex flex-col gap-3 mb-4">
        <div className="flex flex-col sm:flex-row sm:justify-between sm:items-start gap-3">
          <ExportButton
            onExport={handleExport}
            disabled={!data}
            label="Export"
            className="text-xs sm:text-sm px-3 py-2 min-h-[44px] self-start"
          />
        </div>

        {/* View Toggle Buttons */}
        <div
          className="flex flex-wrap gap-2"
          role="group"
          aria-label="Chart view options"
        >
          <button
            onClick={() => setChartView('byType')}
            className={`min-h-[44px] px-3 py-2 text-xs sm:text-sm font-tm-body font-medium rounded-md transition-colors ${
              chartView === 'byType'
                ? 'bg-tm-loyal-blue text-tm-white'
                : 'bg-gray-100 text-gray-700 hover:bg-gray-200'
            }`}
            aria-label="View awards by type"
            aria-pressed={chartView === 'byType'}
          >
            By Type
          </button>
          <button
            onClick={() => setChartView('byMonth')}
            className={`min-h-[44px] px-3 py-2 text-xs sm:text-sm font-tm-body font-medium rounded-md transition-colors ${
              chartView === 'byMonth'
                ? 'bg-tm-loyal-blue text-tm-white'
                : 'bg-gray-100 text-gray-700 hover:bg-gray-200'
            }`}
            aria-label="View awards by month"
            aria-pressed={chartView === 'byMonth'}
          >
            By Month
          </button>
          <button
            onClick={() => setChartView('topClubs')}
            className={`min-h-[44px] px-3 py-2 text-xs sm:text-sm font-tm-body font-medium rounded-md transition-colors ${
              chartView === 'topClubs'
                ? 'bg-tm-loyal-blue text-tm-white'
                : 'bg-gray-100 text-gray-700 hover:bg-gray-200'
            }`}
            aria-label="View top performing clubs"
            aria-pressed={chartView === 'topClubs'}
          >
            Top Clubs
          </button>
        </div>
      </div>

      {/* Chart Display */}
      <div className="mt-4">
        {chartView === 'byType' && (
          <div>
            <h3 className="text-xs sm:text-sm font-tm-headline font-semibold text-tm-black mb-3">
              Awards by Type
            </h3>
            {byTypeData.length > 0 ? (
              <div
                role="img"
                aria-label={byTypeDescription}
                className="w-full overflow-x-auto"
              >
                <div className="min-w-[320px]">
                  <ResponsiveContainer width="100%" height={280} minWidth={320}>
                    <BarChart
                      data={byTypeData}
                      margin={{ top: 5, right: 10, left: 0, bottom: 60 }}
                      aria-hidden="true"
                    >
                      <CartesianGrid
                        strokeDasharray={CHART_STYLES.GRID.strokeDasharray}
                        stroke={CHART_STYLES.GRID.stroke}
                      />
                      <XAxis
                        dataKey="type"
                        stroke={CHART_STYLES.AXIS.stroke}
                        style={{
                          fontSize: CHART_STYLES.AXIS.fontSize,
                          fontFamily: CHART_STYLES.AXIS.fontFamily,
                        }}
                        angle={-45}
                        textAnchor="end"
                        height={80}
                        interval="preserveStartEnd"
                      />
                      <YAxis
                        stroke={CHART_STYLES.AXIS.stroke}
                        style={{
                          fontSize: CHART_STYLES.AXIS.fontSize,
                          fontFamily: CHART_STYLES.AXIS.fontFamily,
                        }}
                        allowDecimals={false}
                        width={40}
                      />
                      <Tooltip
                        content={<ChartTooltip />}
                        contentStyle={CHART_STYLES.TOOLTIP}
                      />
                      <Bar dataKey="count" name="Awards" radius={[8, 8, 0, 0]}>
                        {byTypeData.map((entry, index) => (
                          <Cell key={`cell-${index}`} fill={entry.color} />
                        ))}
                      </Bar>
                    </BarChart>
                  </ResponsiveContainer>
                </div>
              </div>
            ) : (
              <div className="flex items-center justify-center h-80">
                <p className="text-tm-cool-gray font-tm-body">
                  No award type data available
                </p>
              </div>
            )}
          </div>
        )}

        {chartView === 'byMonth' && (
          <div>
            <h3 className="text-xs sm:text-sm font-tm-headline font-semibold text-tm-black mb-3">
              Awards Over Time ({months} Months)
            </h3>
            {byMonthData.length > 0 ? (
              <div
                role="img"
                aria-label={byMonthDescription}
                className="w-full overflow-x-auto"
              >
                <div className="min-w-[320px]">
                  <ResponsiveContainer width="100%" height={280} minWidth={320}>
                    <LineChart
                      data={byMonthData}
                      margin={{ top: 5, right: 10, left: 0, bottom: 60 }}
                      aria-hidden="true"
                    >
                      <CartesianGrid
                        strokeDasharray={CHART_STYLES.GRID.strokeDasharray}
                        stroke={CHART_STYLES.GRID.stroke}
                      />
                      <XAxis
                        dataKey="month"
                        stroke={CHART_STYLES.AXIS.stroke}
                        style={{
                          fontSize: CHART_STYLES.AXIS.fontSize,
                          fontFamily: CHART_STYLES.AXIS.fontFamily,
                        }}
                        angle={-45}
                        textAnchor="end"
                        height={80}
                        interval="preserveStartEnd"
                      />
                      <YAxis
                        stroke={CHART_STYLES.AXIS.stroke}
                        style={{
                          fontSize: CHART_STYLES.AXIS.fontSize,
                          fontFamily: CHART_STYLES.AXIS.fontFamily,
                        }}
                        allowDecimals={false}
                        tickFormatter={value => value.toLocaleString()}
                        width={50}
                      />
                      <Tooltip content={<DateTooltip />} />
                      <Legend
                        wrapperStyle={CHART_STYLES.LEGEND}
                        iconType="line"
                        verticalAlign="top"
                        height={36}
                      />
                      <Line
                        type="monotone"
                        dataKey="awards"
                        stroke={colors[0]}
                        strokeWidth={2}
                        dot={{ fill: colors[0], r: 3 }}
                        activeDot={{ r: 5 }}
                        name="Awards Earned"
                      />
                    </LineChart>
                  </ResponsiveContainer>
                </div>
              </div>
            ) : (
              <div className="flex items-center justify-center h-80">
                <p className="text-tm-cool-gray font-tm-body">
                  No monthly data available
                </p>
              </div>
            )}
          </div>
        )}

        {chartView === 'topClubs' && (
          <div>
            <h3 className="text-xs sm:text-sm font-tm-headline font-semibold text-tm-black mb-3">
              Top Performing Clubs (by Educational Awards)
            </h3>
            {topClubsData.length > 0 ? (
              <div
                role="img"
                aria-label={topClubsDescription}
                className="w-full overflow-x-auto"
              >
                <div className="min-w-[320px]">
                  <ResponsiveContainer width="100%" height={320} minWidth={320}>
                    <BarChart
                      data={topClubsData}
                      margin={{ top: 5, right: 10, left: 0, bottom: 100 }}
                      layout="horizontal"
                      aria-hidden="true"
                    >
                      <CartesianGrid
                        strokeDasharray={CHART_STYLES.GRID.strokeDasharray}
                        stroke={CHART_STYLES.GRID.stroke}
                      />
                      <XAxis
                        dataKey="clubName"
                        stroke={CHART_STYLES.AXIS.stroke}
                        style={{
                          fontSize: '9px',
                          fontFamily: CHART_STYLES.AXIS.fontFamily,
                        }}
                        angle={-45}
                        textAnchor="end"
                        height={120}
                        interval={0}
                      />
                      <YAxis
                        stroke={CHART_STYLES.AXIS.stroke}
                        style={{
                          fontSize: CHART_STYLES.AXIS.fontSize,
                          fontFamily: CHART_STYLES.AXIS.fontFamily,
                        }}
                        allowDecimals={false}
                        width={40}
                      />
                      <Tooltip
                        content={<ChartTooltip />}
                        contentStyle={CHART_STYLES.TOOLTIP}
                      />
                      <Bar dataKey="awards" name="Awards" radius={[8, 8, 0, 0]}>
                        {topClubsData.map((entry, index) => (
                          <Cell key={`cell-${index}`} fill={entry.color} />
                        ))}
