import React from 'react'
import { ExportButton } from './ExportButton'
import { DataStatus } from '../types/reconciliation'

interface EnhancedExportButtonProps {
  onExport: (metadata?: ExportMetadata) => void | Promise<void>
  dataStatus?: DataStatus
  label?: string
  disabled?: boolean
  className?: string
}

export interface ExportMetadata {
  reconciliationStatus: string
  dataCollectionDate: string
  exportTimestamp: string
  isPreliminary: boolean
  isFinal: boolean
  reconciliationPhase?: string
}

/**
 * Enhanced export button that includes reconciliation metadata in exports
 */
export const EnhancedExportButton: React.FC<EnhancedExportButtonProps> = ({
  onExport,
  dataStatus,
  label = 'Export CSV',
  disabled = false,
  className = '',
}) => {
  const handleExport = async () => {
    if (!dataStatus) {
      await onExport()
      return
    }

    const metadata: ExportMetadata = {
      reconciliationStatus: dataStatus.isFinal
        ? 'Final'
        : dataStatus.isPreliminary
          ? 'Preliminary'
          : 'Current',
      dataCollectionDate: dataStatus.dataCollectionDate,
      exportTimestamp: new Date().toISOString(),
      isPreliminary: dataStatus.isPreliminary,
      isFinal: dataStatus.isFinal,
      ...(dataStatus.reconciliationStatus?.phase && {
        reconciliationPhase: dataStatus.reconciliationStatus.phase,
      }),
    }

    await onExport(metadata)
  }

  return (
    <ExportButton
      onExport={handleExport}
      label={label}
      disabled={disabled}
      className={className}
    />
  )
}

import React from 'react'

interface ErrorDisplayProps {
  error: Error | string | null
  onRetry?: () => void
  title?: string
  showDetails?: boolean
  variant?: 'inline' | 'card' | 'full'
  className?: string
}

/**
 * Reusable error display component with retry functionality
 * Provides user-friendly error messages and optional retry actions
 */
export const ErrorDisplay: React.FC<ErrorDisplayProps> = ({
  error,
  onRetry,
  title = 'Error Loading Data',
  showDetails = false,
  variant = 'card',
  className = '',
}) => {
  if (!error) return null

  const errorMessage = typeof error === 'string' ? error : error.message

  // Determine if this is a network error
  const isNetworkError =
    errorMessage.toLowerCase().includes('network') ||
    errorMessage.toLowerCase().includes('fetch') ||
    errorMessage.toLowerCase().includes('connection')

  // Determine if this is a not found error
  const isNotFoundError =
    errorMessage.toLowerCase().includes('404') ||
    errorMessage.toLowerCase().includes('not found')

  const getErrorIcon = () => {
    if (isNetworkError) {
      return (
        <svg
          className="w-6 h-6"
          fill="none"
          stroke="currentColor"
          viewBox="0 0 24 24"
        >
          <path
            strokeLinecap="round"
            strokeLinejoin="round"
            strokeWidth={2}
            d="M18.364 5.636a9 9 0 010 12.728m0 0l-2.829-2.829m2.829 2.829L21 21M15.536 8.464a5 5 0 010 7.072m0 0l-2.829-2.829m-4.243 2.829a4.978 4.978 0 01-1.414-2.83m-1.414 5.658a9 9 0 01-2.167-9.238m7.824 2.167a1 1 0 111.414 1.414m-1.414-1.414L3 3m8.293 8.293l1.414 1.414"
          />
        </svg>
      )
    }
    return (
      <svg
        className="w-6 h-6"
        fill="none"
        stroke="currentColor"
        viewBox="0 0 24 24"
      >
        <path
          strokeLinecap="round"
          strokeLinejoin="round"
          strokeWidth={2}
          d="M12 9v2m0 4h.01m-6.938 4h13.856c1.54 0 2.502-1.667 1.732-3L13.732 4c-.77-1.333-2.694-1.333-3.464 0L3.34 16c-.77 1.333.192 3 1.732 3z"
        />
      </svg>
    )
  }

  const getUserFriendlyMessage = () => {
    if (isNetworkError) {
      return 'Unable to connect to the server. Please check your internet connection and try again.'
    }
    if (isNotFoundError) {
      return 'The requested data could not be found. It may not have been cached yet.'
    }
    return errorMessage
  }

  if (variant === 'inline') {
    return (
      <div
        className={`flex items-center gap-2 text-red-600 text-sm ${className}`}
        role="alert"
      >
        <svg
          className="w-4 h-4 flex-shrink-0"
          fill="none"
          stroke="currentColor"
          viewBox="0 0 24 24"
        >
          <path
            strokeLinecap="round"
            strokeLinejoin="round"
            strokeWidth={2}
            d="M12 8v4m0 4h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z"
          />
        </svg>
        <span>{getUserFriendlyMessage()}</span>
        {onRetry && (
          <button
            onClick={onRetry}
            className="ml-2 tm-btn-secondary text-sm"
            aria-label="Retry loading data"
          >
            Retry
          </button>
        )}
      </div>
    )
  }

  if (variant === 'full') {
    return (
      <div
        className={`tm-card min-h-screen bg-gray-100 flex items-center justify-center px-4 ${className}`}
      >
        <div className="tm-card max-w-md w-full" role="alert">
          <div className="flex items-center justify-center w-12 h-12 mx-auto bg-red-100 rounded-full mb-4">
            <div className="text-red-600">{getErrorIcon()}</div>
          </div>

          <h2 className="text-xl font-bold text-gray-900 text-center mb-2">
            {title}
          </h2>

          <p className="text-gray-600 text-center mb-6">
            {getUserFriendlyMessage()}
          </p>

          {showDetails && errorMessage && (
            <div className="mb-6 p-3 bg-gray-50 rounded border border-gray-200">
              <p className="text-xs text-gray-600 font-mono break-words">
                {errorMessage}
              </p>
            </div>
          )}

          {onRetry && (
            <button
              onClick={onRetry}
              className="tm-btn-primary w-full"
              aria-label="Retry loading data"
            >
              Try Again
            </button>
          )}
        </div>
      </div>
    )
  }

  // card variant (default)
  return (
    <div className={`tm-card ${className}`} role="alert">
      <div className="flex items-start gap-3">
        <div className="flex-shrink-0 text-red-600 mt-0.5">
          {getErrorIcon()}
        </div>
        <div className="flex-1">
          <h3 className="text-sm font-semibold text-red-900 mb-1">{title}</h3>
          <p className="text-sm text-red-800 mb-3">
            {getUserFriendlyMessage()}
          </p>

          {showDetails && errorMessage && (
            <details className="mb-3">
              <summary className="text-xs text-red-700 cursor-pointer hover:text-red-900 font-medium">
                Technical Details
              </summary>
              <p className="mt-2 text-xs text-red-700 font-mono bg-red-100 p-2 rounded break-words">
                {errorMessage}
              </p>
            </details>
          )}

          {onRetry && (
            <button
              onClick={onRetry}
              className="tm-btn-secondary text-sm"
              aria-label="Retry loading data"
            >
              Try Again
            </button>
          )}
        </div>
      </div>
    </div>
  )
}

