  divisionId: fc
    .string({ minLength: 1, maxLength: 5 })
    .filter(s => s.trim().length > 0)
    .map(s => s.trim()),
  divisionName: fc
    .string({ minLength: 2, maxLength: 20 })
    .filter(s => s.trim().length > 1)
    .map(s => s.trim()),
  areaId: fc
    .string({ minLength: 1, maxLength: 5 })
    .filter(s => s.trim().length > 0)
    .map(s => s.trim()),
  areaName: fc
    .string({ minLength: 2, maxLength: 20 })
    .filter(s => s.trim().length > 1)
    .map(s => s.trim()),
  membershipTrend: fc.array(
    fc.record({
      date: fc.constant('2024-01-01'),
