/**
 * Hook to fetch district analytics with caching for common date ranges
 */
export const useDistrictAnalytics = (
  districtId: string | null,
  startDate?: string,
  endDate?: string
) => {
  return useQuery<DistrictAnalytics, Error>({
    queryKey: ['districtAnalytics', districtId, startDate, endDate],
    queryFn: async () => {
      if (!districtId) {
        throw new Error('District ID is required')
      }

      const params = new URLSearchParams()
      if (startDate) params.append('startDate', startDate)
      if (endDate) params.append('endDate', endDate)

      const response = await apiClient.get<DistrictAnalytics>(
        `/districts/${districtId}/analytics${params.toString() ? `?${params.toString()}` : ''}`
      )
      return response.data
    },
    enabled: !!districtId,
    staleTime: 10 * 60 * 1000, // 10 minutes - cache analytics calculations longer
    gcTime: 30 * 60 * 1000, // 30 minutes - keep in cache for common date ranges
    retry: (failureCount, error: unknown) => {
      // Don't retry on 404 (no data) or 400 (bad request)
      if (error && typeof error === 'object' && 'response' in error) {
        const axiosError = error as { response?: { status?: number } }
        if (
          axiosError.response?.status === 404 ||
          axiosError.response?.status === 400
        ) {
          return false
        }
      }
      // Retry up to 2 times for other errors
      return failureCount < 2
    },
    retryDelay: attemptIndex => Math.min(1000 * 2 ** attemptIndex, 30000), // Exponential backoff
  })
}

import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query'
import { apiClient } from '../services/api'

interface DistrictBackfillRequest {
  startDate?: string
  endDate?: string
}

interface BackfillProgress {
  total: number
  completed: number
  skipped: number
  unavailable: number
  failed: number
  current: string
}

interface DistrictBackfillResponse {
  backfillId: string
  districtId: string
  status: 'processing' | 'complete' | 'error'
  progress: BackfillProgress
  error?: string
}

/**
 * Hook to initiate a district backfill
 */
export function useInitiateDistrictBackfill(districtId: string) {
  return useMutation({
    mutationFn: async (request: DistrictBackfillRequest) => {
      const response = await apiClient.post<DistrictBackfillResponse>(
        `/districts/${districtId}/backfill`,
        request
      )
      return response.data
    },
  })
}

/**
 * Hook to poll district backfill status
 * Automatically polls every 2 seconds while backfill is processing
 */
export function useDistrictBackfillStatus(
  districtId: string,
  backfillId: string | null,
  enabled: boolean = true
) {
  return useQuery({
    queryKey: ['districtBackfillStatus', districtId, backfillId],
    queryFn: async () => {
      if (!backfillId) {
        throw new Error('No backfill ID provided')
      }
      const response = await apiClient.get<DistrictBackfillResponse>(
        `/districts/${districtId}/backfill/${backfillId}`
      )
      return response.data
    },
    enabled: enabled && !!backfillId,
    refetchInterval: query => {
      const data = query.state.data
      // Stop polling if backfill is complete or errored
      if (data?.status === 'complete' || data?.status === 'error') {
        return false
      }
      // Poll every 2 seconds while processing
      return 2000
    },
    refetchIntervalInBackground: true,
    staleTime: 0, // Always consider data stale to ensure fresh polling
  })
}

/**
 * Hook to cancel a district backfill
 */
export function useCancelDistrictBackfill(districtId: string) {
  const queryClient = useQueryClient()

  return useMutation({
    mutationFn: async (backfillId: string) => {
      await apiClient.delete(`/districts/${districtId}/backfill/${backfillId}`)
    },
    onSuccess: (_data, backfillId) => {
      // Invalidate the status query to stop polling
      queryClient.invalidateQueries({
        queryKey: ['districtBackfillStatus', districtId, backfillId],
      })
      // Invalidate cached dates to refresh the list
      queryClient.invalidateQueries({
        queryKey: ['district-cached-dates', districtId],
      })
    },
  })
}

import { useState, useCallback } from 'react'
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query'
import { apiClient } from '../services/api'

/**
 * District configuration data structure
 */
export interface DistrictConfiguration {
  configuredDistricts: string[]
  lastUpdated: string
  updatedBy: string
  version: number
}

/**
 * Configuration change record for audit trail
 */
export interface ConfigurationChange {
  timestamp: string
  action: 'add' | 'remove' | 'replace'
  districtId: string | null
  adminUser: string
  previousDistricts?: string[]
  newDistricts?: string[]
  context?: string
}

/**
 * District collection information
 */
export interface DistrictCollectionInfo {
  districtId: string
  lastSuccessfulCollection: string | null
  status: 'valid' | 'invalid' | 'unknown'
  recentSuccessCount: number
}

/**
 * Configuration validation result
 */
export interface ConfigurationValidationResult {
  isValid: boolean
  configuredDistricts: string[]
  validDistricts: string[]
  invalidDistricts: string[]
  warnings: string[]
  lastCollectionInfo: DistrictCollectionInfo[]
}

/**
 * District configuration response from API
 */
export interface DistrictConfigurationResponse {
  configuration: DistrictConfiguration
  status: {
    hasConfiguredDistricts: boolean
    totalDistricts: number
  }
  validation: ConfigurationValidationResult
  metadata: {
    operation_id: string
    duration_ms: number
  }
}

/**
 * Hook for fetching district configuration
 */
export function useDistrictConfiguration() {
  return useQuery({
    queryKey: ['district-configuration'],
    queryFn: async (): Promise<DistrictConfigurationResponse> => {
      const response = await apiClient.get('/admin/districts/config')
      return response.data
    },
    staleTime: 5 * 60 * 1000, // 5 minutes
  })
}

/**
 * Hook for adding districts to configuration
 */
export function useAddDistricts() {
  const queryClient = useQueryClient()

  return useMutation({
    mutationFn: async ({
      districtIds,
      replace = false,
    }: {
      districtIds: string[]
      replace?: boolean
    }) => {
      const response = await apiClient.post('/admin/districts/config', {
        districtIds,
        replace,
      })
      return response.data
    },
    onSuccess: () => {
      // Invalidate and refetch district configuration
      queryClient.invalidateQueries({ queryKey: ['district-configuration'] })
      // Also invalidate rankings since district changes affect snapshots
      queryClient.invalidateQueries({ queryKey: ['district-rankings'] })
    },
  })
}

/**
 * Hook for removing a district from configuration
 */
export function useRemoveDistrict() {
  const queryClient = useQueryClient()

  return useMutation({
    mutationFn: async (districtId: string) => {
      const response = await apiClient.delete(
        `/admin/districts/config/${districtId}`
      )
      return response.data
    },
    onSuccess: () => {
      // Invalidate and refetch district configuration
      queryClient.invalidateQueries({ queryKey: ['district-configuration'] })
      // Also invalidate rankings since district changes affect snapshots
      queryClient.invalidateQueries({ queryKey: ['district-rankings'] })
    },
  })
}

/**
 * Hook for managing district configuration with local state
 */
export function useDistrictConfigurationManager() {
  const [editMode, setEditMode] = useState(false)
  const [localDistricts, setLocalDistricts] = useState<string[]>([])
  const [newDistrictInput, setNewDistrictInput] = useState('')

  const { data, isLoading, error, refetch } = useDistrictConfiguration()
  const addDistrictsMutation = useAddDistricts()
  const removeDistrictMutation = useRemoveDistrict()

  // Initialize local state when data loads
  const initializeLocalState = useCallback(() => {
    if (data?.configuration?.configuredDistricts) {
      setLocalDistricts([...data.configuration.configuredDistricts])
    }
  }, [data])

  // Add district to local state
  const addDistrictLocally = useCallback(
    (districtId: string) => {
      const cleanId = districtId.trim().toUpperCase()
      if (cleanId && !localDistricts.includes(cleanId)) {
        setLocalDistricts(prev => [...prev, cleanId].sort())
      }
    },
    [localDistricts]
  )

  // Remove district from local state
  const removeDistrictLocally = useCallback((districtId: string) => {
    setLocalDistricts(prev => prev.filter(id => id !== districtId))
  }, [])

  // Save changes to server
  const saveChanges = useCallback(async () => {
    try {
      await addDistrictsMutation.mutateAsync({
        districtIds: localDistricts,
        replace: true,
      })
      setEditMode(false)
      return true
    } catch (error) {
      console.error('Failed to save district configuration:', error)
      return false
    }
  }, [localDistricts, addDistrictsMutation])

  // Cancel changes and revert to server state
  const cancelChanges = useCallback(() => {
    initializeLocalState()
    setEditMode(false)
    setNewDistrictInput('')
  }, [initializeLocalState])

  // Add single district immediately (not in edit mode)
  const addDistrict = useCallback(
    async (districtId: string) => {
      try {
        await addDistrictsMutation.mutateAsync({
          districtIds: [districtId],
          replace: false,
        })
        return true
      } catch (error) {
        console.error('Failed to add district:', error)
        return false
      }
    },
    [addDistrictsMutation]
  )

  // Remove single district immediately (not in edit mode)
  const removeDistrict = useCallback(
    async (districtId: string) => {
      try {
        await removeDistrictMutation.mutateAsync(districtId)
        return true
      } catch (error) {
        console.error('Failed to remove district:', error)
        return false
      }
    },
    [removeDistrictMutation]
  )

  return {
    // Data
    data,
    isLoading,
    error,
    refetch,

    // Edit mode state
    editMode,
    setEditMode,
    localDistricts,
    newDistrictInput,
    setNewDistrictInput,

    // Local state management
    initializeLocalState,
    addDistrictLocally,
    removeDistrictLocally,

    // Server operations
    saveChanges,
    cancelChanges,
    addDistrict,
    removeDistrict,

    // Mutation states
    isSaving: addDistrictsMutation.isPending,
    isRemoving: removeDistrictMutation.isPending,
    saveError: addDistrictsMutation.error,
    removeError: removeDistrictMutation.error,
  }
}

import { useQuery } from '@tanstack/react-query'
import { apiClient } from '../services/api'

/**
 * Interface for club performance data
 */
export interface ClubPerformance {
  'Club Number': string
  'Club Name': string
  Division: string
  Area: string
  'Active Members': string
  'Goals Met': string
  'Club Status': string
  'Club Distinguished Status': string
  'Mem. Base': string
  [key: string]: string // Allow additional dynamic fields
}

/**
 * Interface for division performance data
 */
export interface DivisionPerformance {
  Division: string
  'Total Clubs': string
  'Total Members': string
  'Goals Met': string
  [key: string]: string // Allow additional dynamic fields
}

/**
 * Interface for district performance data
 */
export interface DistrictPerformance {
  District: string
  'Total Clubs': string
  'Total Members': string
  'Goals Met': string
  'Distinguished Clubs': string
  [key: string]: string // Allow additional dynamic fields
}

/**
 * Interface for district cache entry containing all three report types
 */
export interface DistrictCacheEntry {
  districtId: string
  date: string
  districtPerformance: DistrictPerformance[]
  divisionPerformance: DivisionPerformance[]
  clubPerformance: ClubPerformance[]
  fetchedAt: string
}

/**
 * Interface for cached dates response
 */
export interface CachedDatesResponse {
  districtId: string
  dates: string[]
  count: number
  dateRange: {
    startDate: string
    endDate: string
  } | null
}

/**
 * Hook to fetch district data for a specific date
 * Retrieves cached district data including district, division, and club performance
 *
 * @param districtId - The district ID to fetch data for
 * @param date - The date in YYYY-MM-DD format
 * @param enabled - Whether the query should be enabled (default: true)
 * @returns Query result with district cache entry
 *
 * Requirements: 1.4
 */
export const useDistrictData = (
  districtId: string | null,
  date: string | null,
  enabled: boolean = true
) => {
  return useQuery<DistrictCacheEntry, Error>({
    queryKey: ['districtData', districtId, date],
    queryFn: async () => {
      if (!districtId || !date) {
        throw new Error('District ID and date are required')
      }

      const response = await apiClient.get<DistrictCacheEntry>(
        `/districts/${districtId}/data/${date}`
      )
      return response.data
    },
    enabled: enabled && !!districtId && !!date,
    staleTime: 10 * 60 * 1000, // 10 minutes - data is historical and doesn't change
    retry: (failureCount, error: unknown) => {
      // Don't retry on 404 (no data) or 400 (bad request)
      if (error && typeof error === 'object' && 'response' in error) {
        const axiosError = error as { response?: { status?: number } }
        if (
          axiosError.response?.status === 404 ||
          axiosError.response?.status === 400
        ) {
          return false
        }
      }
      // Retry up to 2 times for network errors
      return failureCount < 2
    },
    retryDelay: attemptIndex => Math.min(1000 * 2 ** attemptIndex, 30000),
  })
}

/**
 * Hook to fetch all cached dates for a district
 * Returns list of dates that have cached data available
 *
 * @param districtId - The district ID to fetch cached dates for
 * @param enabled - Whether the query should be enabled (default: true)
 * @returns Query result with cached dates and date range
 *
 * Requirements: 1.4
 */
export const useDistrictCachedDates = (
  districtId: string | null,
  enabled: boolean = true
) => {
  return useQuery<CachedDatesResponse, Error>({
    queryKey: ['district-cached-dates', districtId],
    queryFn: async () => {
      if (!districtId) {
        throw new Error('District ID is required')
      }

      const response = await apiClient.get<CachedDatesResponse>(
        `/districts/${districtId}/cached-dates`
      )
      return response.data
    },
    enabled: enabled && !!districtId,
    staleTime: 5 * 60 * 1000, // 5 minutes
    retry: (failureCount, error: unknown) => {
      // Don't retry on 404 (no data) or 400 (bad request)
      if (error && typeof error === 'object' && 'response' in error) {
        const axiosError = error as { response?: { status?: number } }
        if (
          axiosError.response?.status === 404 ||
          axiosError.response?.status === 400
        ) {
          return false
        }
      }
      // Retry up to 2 times for network errors
      return failureCount < 2
    },
    retryDelay: attemptIndex => Math.min(1000 * 2 ** attemptIndex, 30000),
  })
}

