/**
 * Responsive Design Hook
 *
 * This hook provides utilities for managing responsive design compliance
 * according to Toastmasters brand guidelines across all breakpoints.
 *
 * Requirements: 6.1, 6.2, 6.3, 6.4, 6.5
 */

import React, { useState, useEffect, useCallback, useRef } from 'react'
import { BREAKPOINTS } from '../components/brand/types'
import { useTouchTarget } from './useTouchTarget'
import { useContrastCheck } from './useContrastCheck'
import type { ContrastValidationResult } from '../utils/contrastCalculator'

export type BreakpointName = 'mobile' | 'tablet' | 'desktop' | 'wide'

export interface ResponsiveState {
  breakpoint: BreakpointName
  width: number
  height: number
  isMobile: boolean
  isTablet: boolean
  isDesktop: boolean
  isWide: boolean
  touchTargetSize: number
  minFontSize: number
  containerPadding: string
}

export interface ResponsiveValidationResult {
  touchTargetsValid: boolean
  fontSizesValid: boolean
  contrastValid: boolean
  brandColorsValid: boolean
  overallValid: boolean
  violations: ResponsiveViolation[]
}

export interface ResponsiveViolation {
  type: 'touch-target' | 'font-size' | 'contrast' | 'brand-color'
  element: HTMLElement
  message: string
  recommendation: string
  breakpoint: BreakpointName
}

export interface ResponsiveDesignOptions {
  enableValidation?: boolean
  enableAutoFix?: boolean
  onViolation?: (violation: ResponsiveViolation) => void
  onBreakpointChange?: (breakpoint: BreakpointName) => void
}

/**
 * Hook for managing responsive design compliance
 */
export function useResponsiveDesign(options: ResponsiveDesignOptions = {}) {
  const {
    enableValidation = true,
    enableAutoFix = false,
    onViolation,
    onBreakpointChange,
  } = options

  const [responsiveState, setResponsiveState] = useState<ResponsiveState>(() =>
    getResponsiveState()
  )
  const [validationResult, setValidationResult] =
    useState<ResponsiveValidationResult>({
      touchTargetsValid: true,
      fontSizesValid: true,
      contrastValid: true,
      brandColorsValid: true,
      overallValid: true,
      violations: [],
    })

  const touchTarget = useTouchTarget({
    minSize: responsiveState.touchTargetSize,
    onViolation: result => {
      if (onViolation) {
        onViolation({
          type: 'touch-target',
          element: result.element,
          message: `Touch target too small: ${result.width}x${result.height}px`,
          recommendation: result.recommendation || 'Increase element size',
          breakpoint: responsiveState.breakpoint,
        })
      }
    },
  })

  const contrastCheck = useContrastCheck({
    onViolation: result => {
      if (onViolation) {
        // Create a mock element for the violation since ContrastValidationResult doesn't include element
        const mockElement = document.createElement('div')
        onViolation({
          type: 'contrast',
          element: mockElement,
          message: `Contrast ratio too low: ${result.ratio.toFixed(2)}:1`,
          recommendation: 'Use higher contrast colors or adjust opacity',
          breakpoint: responsiveState.breakpoint,
        })
      }
    },
  })

  const validationTimeoutRef = useRef<ReturnType<typeof setTimeout> | null>(
    null
  )

  /**
   * Get current responsive state based on viewport
   */
  function getResponsiveState(): ResponsiveState {
    const width = typeof window !== 'undefined' ? window.innerWidth : 1024
    const height = typeof window !== 'undefined' ? window.innerHeight : 768

    let breakpoint: BreakpointName = 'mobile'
    let touchTargetSize = 44
    let minFontSize = 14
    let containerPadding = '16px'

    if (width >= parseInt(BREAKPOINTS.wide)) {
      breakpoint = 'wide'
      touchTargetSize = 48
      minFontSize = 14
      containerPadding = '48px'
    } else if (width >= parseInt(BREAKPOINTS.desktop)) {
      breakpoint = 'desktop'
      touchTargetSize = 48
      minFontSize = 14
      containerPadding = '32px'
    } else if (width >= parseInt(BREAKPOINTS.tablet)) {
      breakpoint = 'tablet'
      touchTargetSize = 44
      minFontSize = 14
      containerPadding = '24px'
    } else {
      breakpoint = 'mobile'
      touchTargetSize = 44
      minFontSize = 14
      containerPadding = '16px'
    }

    return {
      breakpoint,
      width,
      height,
      isMobile: breakpoint === 'mobile',
      isTablet: breakpoint === 'tablet',
      isDesktop: breakpoint === 'desktop',
      isWide: breakpoint === 'wide',
      touchTargetSize,
      minFontSize,
      containerPadding,
    }
  }

  /**
   * Handle window resize
   */
  const handleResize = useCallback(() => {
    const newState = getResponsiveState()
    const previousBreakpoint = responsiveState.breakpoint

    setResponsiveState(newState)

    if (newState.breakpoint !== previousBreakpoint && onBreakpointChange) {
      onBreakpointChange(newState.breakpoint)
    }

    // Debounce validation on resize
    if (enableValidation) {
      if (validationTimeoutRef.current) {
        clearTimeout(validationTimeoutRef.current)
      }
      validationTimeoutRef.current = setTimeout(() => {
        validateResponsiveCompliance()
      }, 300)
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [responsiveState.breakpoint, enableValidation, onBreakpointChange])

  /**
   * Validate brand color usage
   */
  const validateBrandColors = useCallback(
    (container: HTMLElement): ResponsiveViolation[] => {
      const violations: ResponsiveViolation[] = []
      const brandColors = [
        '#004165', // TM Loyal Blue
        '#772432', // TM True Maroon
        '#A9B2B1', // TM Cool Gray
        '#F2DF74', // TM Happy Yellow
        '#000000', // TM Black
        '#FFFFFF', // TM White
      ]

      const allElements = container.querySelectorAll('*')
      allElements.forEach(element => {
        const htmlElement = element as HTMLElement
        const computedStyle = window.getComputedStyle(htmlElement)

        // Check background color
        const bgColor = computedStyle.backgroundColor
        if (
          bgColor &&
          bgColor !== 'transparent' &&
          bgColor !== 'rgba(0, 0, 0, 0)'
        ) {
          const isValidBrandColor = brandColors.some(brandColor => {
            // Simple color matching - in production, you'd want more sophisticated color comparison
            return bgColor.includes(brandColor.toLowerCase())
          })

          if (!isValidBrandColor) {
            violations.push({
              type: 'brand-color',
              element: htmlElement,
              message: `Non-brand background color detected: ${bgColor}`,
              recommendation: 'Use only Toastmasters brand palette colors',
              breakpoint: responsiveState.breakpoint,
            })
          }
        }

        // Check text color
        const textColor = computedStyle.color
        if (textColor && textColor !== 'inherit') {
          const isValidBrandColor = brandColors.some(brandColor => {
            return textColor.includes(brandColor.toLowerCase())
          })

          if (!isValidBrandColor) {
            violations.push({
              type: 'brand-color',
              element: htmlElement,
              message: `Non-brand text color detected: ${textColor}`,
              recommendation: 'Use only Toastmasters brand palette colors',
              breakpoint: responsiveState.breakpoint,
            })
          }
        }
      })

      return violations
    },
    [responsiveState.breakpoint]
  )

  /**
   * Validate responsive design compliance
   */
  const validateResponsiveCompliance = useCallback(
    (container: HTMLElement = document.body): ResponsiveValidationResult => {
      const violations: ResponsiveViolation[] = []

      // Validate touch targets
      const touchTargetResults = touchTarget.validateAllTouchTargets(container)
      const touchTargetViolations = touchTargetResults
        .filter(result => !result.passes)
        .map(result => ({
          type: 'touch-target' as const,
          element: result.element,
          message: `Touch target too small: ${result.width.toFixed(1)}x${result.height.toFixed(1)}px`,
          recommendation:
            result.recommendation ||
            'Increase element size to meet minimum requirements',
          breakpoint: responsiveState.breakpoint,
        }))

      violations.push(...touchTargetViolations)

      // Validate font sizes
      const textElements = container.querySelectorAll(
        'p, span, div, label, input, textarea, select, button'
      )
      const fontSizeViolations: ResponsiveViolation[] = []

      textElements.forEach(element => {
        const htmlElement = element as HTMLElement
        const computedStyle = window.getComputedStyle(htmlElement)
        const fontSize = parseFloat(computedStyle.fontSize)

        if (fontSize < responsiveState.minFontSize) {
          fontSizeViolations.push({
            type: 'font-size',
            element: htmlElement,
            message: `Font size too small: ${fontSize}px (minimum: ${responsiveState.minFontSize}px)`,
            recommendation: `Increase font size to at least ${responsiveState.minFontSize}px`,
            breakpoint: responsiveState.breakpoint,
          })
        }
      })

      violations.push(...fontSizeViolations)

      // Validate contrast ratios
      const contrastResults = contrastCheck.validateAllContrasts(container)
      const contrastViolations = contrastResults
        .filter((result: ContrastValidationResult) => !result.passes)
        .map((result: ContrastValidationResult) => ({
          type: 'contrast' as const,
          element: document.createElement('div'), // Mock element since ContrastValidationResult doesn't include element
          message: `Contrast ratio too low: ${result.ratio.toFixed(2)}:1 (minimum: 4.5:1)`,
          recommendation: 'Use higher contrast colors or adjust opacity',
          breakpoint: responsiveState.breakpoint,
        }))

      violations.push(...contrastViolations)

      // Validate brand colors
      const brandColorViolations = validateBrandColors(container)
      violations.push(...brandColorViolations)

      const result: ResponsiveValidationResult = {
        touchTargetsValid: touchTargetViolations.length === 0,
        fontSizesValid: fontSizeViolations.length === 0,
        contrastValid: contrastViolations.length === 0,
        brandColorsValid: brandColorViolations.length === 0,
        overallValid: violations.length === 0,
        violations,
      }

      setValidationResult(result)

      // Report violations
      violations.forEach(violation => {
        if (onViolation) {
          onViolation(violation)
        }
      })

      return result
    },
    [
      responsiveState,
      touchTarget,
      contrastCheck,
      onViolation,
      validateBrandColors,
    ]
  )

  /**
   * Auto-fix responsive violations
   */
  const autoFixViolations = useCallback(
    (
      violations: ResponsiveViolation[] = validationResult.violations
    ): number => {
      let fixedCount = 0

      violations.forEach(violation => {
        try {
          switch (violation.type) {
            case 'touch-target':
              if (touchTarget.autoFixTouchTarget(violation.element)) {
                fixedCount++
              }
              break

            case 'font-size': {
              const currentSize = parseFloat(
                window.getComputedStyle(violation.element).fontSize
              )
              if (currentSize < responsiveState.minFontSize) {
                violation.element.style.fontSize = `${responsiveState.minFontSize}px`
                fixedCount++
              }
              break
            }

            case 'contrast': {
              // Auto-fix contrast by adjusting opacity or switching to high contrast colors
              const style = window.getComputedStyle(violation.element)
              const bgColor = style.backgroundColor

              // Simple fix: use high contrast brand colors
              if (bgColor.includes('rgb')) {
                violation.element.style.backgroundColor = 'var(--tm-loyal-blue)'
                violation.element.style.color = 'var(--tm-white)'
                fixedCount++
              }
              break
            }

            case 'brand-color': {
              // Auto-fix by replacing with nearest brand color
              const style2 = window.getComputedStyle(violation.element)
              if (style2.backgroundColor !== 'transparent') {
                violation.element.style.backgroundColor = 'var(--tm-cool-gray)'
              }
              if (style2.color !== 'inherit') {
                violation.element.style.color = 'var(--tm-black)'
              }
              fixedCount++
              break
            }
          }
        } catch (error) {
          console.warn('Failed to auto-fix violation:', error)
        }
      })

      return fixedCount
    },
    [validationResult.violations, touchTarget, responsiveState.minFontSize]
  )

  /**
   * Get responsive CSS classes for an element
   */
  const getResponsiveClasses = useCallback(
    (baseClasses: string = ''): string => {
      const responsiveClasses = [
        baseClasses,
        'tm-responsive-element',
        `tm-breakpoint-${responsiveState.breakpoint}`,
      ]

      return responsiveClasses.filter(Boolean).join(' ')
    },
    [responsiveState.breakpoint]
  )

  /**
   * Get responsive styles for an element
   */
  const getResponsiveStyles = useCallback(
    (baseStyles: React.CSSProperties = {}): React.CSSProperties => {
      return {
        ...baseStyles,
        minHeight: `${responsiveState.touchTargetSize}px`,
        fontSize: `max(${responsiveState.minFontSize}px, 1rem)`,
        padding: responsiveState.containerPadding,
      }
    },
    [responsiveState]
  )

  /**
   * Check if current breakpoint matches
   */
  const isBreakpoint = useCallback(
    (breakpoint: BreakpointName | BreakpointName[]): boolean => {
      if (Array.isArray(breakpoint)) {
        return breakpoint.includes(responsiveState.breakpoint)
      }
      return responsiveState.breakpoint === breakpoint
    },
    [responsiveState.breakpoint]
  )

  // Set up resize listener
  useEffect(() => {
    if (typeof window === 'undefined') return

    window.addEventListener('resize', handleResize)

    // Initial validation
    if (enableValidation) {
      setTimeout(() => validateResponsiveCompliance(), 100)
    }

    return () => {
      window.removeEventListener('resize', handleResize)
      if (validationTimeoutRef.current) {
        clearTimeout(validationTimeoutRef.current)
      }
    }
  }, [handleResize, enableValidation, validateResponsiveCompliance])

  // Auto-fix violations if enabled
  useEffect(() => {
    if (enableAutoFix && validationResult.violations.length > 0) {
      const fixedCount = autoFixViolations()
      if (fixedCount > 0) {
        // Re-validate after auto-fix
        setTimeout(() => validateResponsiveCompliance(), 100)
      }
    }
  }, [
    enableAutoFix,
    validationResult.violations,
    autoFixViolations,
    validateResponsiveCompliance,
  ])

  return {
    // State
    ...responsiveState,
    validationResult,

    // Methods
    validateResponsiveCompliance,
    autoFixViolations,
    getResponsiveClasses,
    getResponsiveStyles,
    isBreakpoint,

    // Touch target utilities
    checkTouchTarget: touchTarget.checkTouchTarget,
    registerTouchTarget: touchTarget.registerElement,
    unregisterTouchTarget: touchTarget.unregisterElement,

    // Contrast utilities
    checkContrast: contrastCheck.checkContrast,
    validateAllContrasts: contrastCheck.validateAllContrasts,
  }
}

/**
 * Hook for automatically validating responsive design in a container
 */
export function useAutoResponsiveValidation(
  containerRef: React.RefObject<HTMLElement>,
  options: ResponsiveDesignOptions = {}
) {
  const responsive = useResponsiveDesign(options)
  const [isValidating, setIsValidating] = useState(false)

  useEffect(() => {
    if (!containerRef.current) return

    // Use setTimeout to avoid synchronous setState in effect
    const timeoutId = setTimeout(() => {
      setIsValidating(true)
      responsive.validateResponsiveCompliance(containerRef.current!)
      setIsValidating(false)
    }, 0)

    return () => clearTimeout(timeoutId)
  }, [containerRef, responsive])

  return {
    ...responsive,
    isValidating,
  }
}

/**
 * Utility function to get breakpoint name from width
 */
export function getBreakpointFromWidth(width: number): BreakpointName {
  if (width >= parseInt(BREAKPOINTS.wide)) return 'wide'
  if (width >= parseInt(BREAKPOINTS.desktop)) return 'desktop'
  if (width >= parseInt(BREAKPOINTS.tablet)) return 'tablet'
  return 'mobile'
}

/**
 * Utility function to check if width is mobile
 */
export function isMobileWidth(width: number): boolean {
  return width < parseInt(BREAKPOINTS.tablet)
}

/**
 * Utility function to get responsive font size
 */
export function getResponsiveFontSize(
  baseSize: number,
  breakpoint: BreakpointName
): number {
  const minSize = Math.max(14, baseSize * 0.75)
  const maxSize = baseSize * 1.25

  switch (breakpoint) {
    case 'mobile':
      return minSize
    case 'tablet':
      return baseSize
    case 'desktop':
      return baseSize * 1.125
    case 'wide':
      return Math.min(maxSize, baseSize * 1.25)
    default:
      return baseSize
  }
}

/**
 * Utility function to get responsive spacing
 */
export function getResponsiveSpacing(breakpoint: BreakpointName): string {
  switch (breakpoint) {
    case 'mobile':
      return '16px'
    case 'tablet':
      return '24px'
    case 'desktop':
      return '32px'
    case 'wide':
      return '48px'
    default:
      return '16px'
  }
}

/**
 * Utility function to get responsive touch target size
 */
export function getResponsiveTouchTargetSize(
  breakpoint: BreakpointName
): number {
  switch (breakpoint) {
    case 'mobile':
    case 'tablet':
      return 44
    case 'desktop':
    case 'wide':
      return 48
    default:
      return 44
  }
}

/**
 * Touch Target Validation Hook
 *
 * This hook provides utilities for validating that interactive elements
 * meet the minimum 44px touch target requirement for accessibility.
 *
 * Requirements: 3.2 - Touch Target Accessibility
 */

import React, { useCallback, useEffect, useRef, useState } from 'react'

export interface TouchTargetResult {
  width: number
  height: number
  passes: boolean
  element: HTMLElement
  recommendation?: string
}

export interface TouchTargetValidationOptions {
  minSize?: number
  includeMargin?: boolean
  checkOnResize?: boolean
  onViolation?: (result: TouchTargetResult) => void
}

/**
 * Hook for validating touch target accessibility
 */
export function useTouchTarget(options: TouchTargetValidationOptions = {}) {
  const {
    minSize = 44,
    includeMargin = true,
    checkOnResize = true,
    onViolation,
  } = options

  const [violations, setViolations] = useState<TouchTargetResult[]>([])
  const observerRef = useRef<ResizeObserver | null>(null)
  const elementsRef = useRef<Set<HTMLElement>>(new Set())

  /**
   * Check if an element meets touch target requirements
   */
  const checkTouchTarget = useCallback(
    (element: HTMLElement): TouchTargetResult => {
      const rect = element.getBoundingClientRect()
      let effectiveWidth = rect.width
      let effectiveHeight = rect.height

      if (includeMargin) {
        const computedStyle = window.getComputedStyle(element)
        const marginLeft = parseFloat(computedStyle.marginLeft) || 0
        const marginRight = parseFloat(computedStyle.marginRight) || 0
        const marginTop = parseFloat(computedStyle.marginTop) || 0
        const marginBottom = parseFloat(computedStyle.marginBottom) || 0

        effectiveWidth += marginLeft + marginRight
        effectiveHeight += marginTop + marginBottom
      }

      const passes = effectiveWidth >= minSize && effectiveHeight >= minSize

      let recommendation: string | undefined
      if (!passes) {
        const widthDeficit = Math.max(0, minSize - effectiveWidth)
        const heightDeficit = Math.max(0, minSize - effectiveHeight)

        if (widthDeficit > 0 && heightDeficit > 0) {
          recommendation = `Increase width by ${widthDeficit.toFixed(1)}px and height by ${heightDeficit.toFixed(1)}px to meet ${minSize}px minimum`
        } else if (widthDeficit > 0) {
          recommendation = `Increase width by ${widthDeficit.toFixed(1)}px to meet ${minSize}px minimum`
        } else {
          recommendation = `Increase height by ${heightDeficit.toFixed(1)}px to meet ${minSize}px minimum`
        }
      }

      return {
        width: effectiveWidth,
        height: effectiveHeight,
        passes,
        element,
        ...(recommendation && { recommendation }),
      }
    },
    [minSize, includeMargin]
  )

  /**
   * Validate all interactive elements in a container
   */
  const validateAllTouchTargets = useCallback(
    (container: HTMLElement = document.body): TouchTargetResult[] => {
      const interactiveSelectors = [
        'button',
        'a[href]',
        'input:not([type="hidden"])',
        'select',
        'textarea',
        '[tabindex]:not([tabindex="-1"])',
        '[role="button"]',
        '[role="link"]',
        '[role="menuitem"]',
        '[role="tab"]',
        '[onclick]',
      ]

      const elements = container.querySelectorAll(
        interactiveSelectors.join(', ')
      )
      const results: TouchTargetResult[] = []

      elements.forEach(element => {
        const htmlElement = element as HTMLElement

        // Skip hidden elements
        const computedStyle = window.getComputedStyle(htmlElement)
        if (
          computedStyle.display === 'none' ||
          computedStyle.visibility === 'hidden'
        ) {
          return
        }

        const result = checkTouchTarget(htmlElement)
        results.push(result)

        if (!result.passes && onViolation) {
          onViolation(result)
        }
      })

      return results
    },
    [checkTouchTarget, onViolation]
  )

  /**
   * Register an element for continuous monitoring
   */
  const registerElement = useCallback(
    (element: HTMLElement) => {
      elementsRef.current.add(element)

      if (checkOnResize && !observerRef.current) {
        observerRef.current = new ResizeObserver(entries => {
          const newViolations: TouchTargetResult[] = []

          entries.forEach(entry => {
            const element = entry.target as HTMLElement
            const result = checkTouchTarget(element)

            if (!result.passes) {
              newViolations.push(result)
              if (onViolation) {
                onViolation(result)
              }
            }
          })

          setViolations(prev => {
            // Remove old violations for these elements and add new ones
            const elementSet = new Set(entries.map(entry => entry.target))
            const filtered = prev.filter(v => !elementSet.has(v.element))
            return [...filtered, ...newViolations]
          })
        })
      }

      if (observerRef.current) {
        observerRef.current.observe(element)
      }

      // Initial check
      const result = checkTouchTarget(element)
      if (!result.passes) {
        setViolations(prev => [
          ...prev.filter(v => v.element !== element),
          result,
        ])
        if (onViolation) {
          onViolation(result)
        }
      }
    },
    [checkTouchTarget, checkOnResize, onViolation]
  )

  /**
   * Unregister an element from monitoring
   */
  const unregisterElement = useCallback((element: HTMLElement) => {
    elementsRef.current.delete(element)

    if (observerRef.current) {
      observerRef.current.unobserve(element)
    }

    setViolations(prev => prev.filter(v => v.element !== element))
  }, [])

  /**
   * Get current violations
   */
  const getCurrentViolations = useCallback(() => {
    return violations
  }, [violations])

  /**
   * Check all registered elements
   */
  const recheckAll = useCallback(() => {
    const newViolations: TouchTargetResult[] = []

    elementsRef.current.forEach(element => {
      const result = checkTouchTarget(element)
      if (!result.passes) {
        newViolations.push(result)
        if (onViolation) {
          onViolation(result)
        }
      }
    })

    setViolations(newViolations)
  }, [checkTouchTarget, onViolation])

  /**
   * Auto-fix touch target by adding padding
   */
  const autoFixTouchTarget = useCallback(
    (element: HTMLElement): boolean => {
      const result = checkTouchTarget(element)
      if (result.passes) return true

      const widthDeficit = Math.max(0, minSize - result.width)
      const heightDeficit = Math.max(0, minSize - result.height)

      if (widthDeficit > 0 || heightDeficit > 0) {
        const computedStyle = window.getComputedStyle(element)
        const currentPaddingLeft = parseFloat(computedStyle.paddingLeft) || 0
        const currentPaddingRight = parseFloat(computedStyle.paddingRight) || 0
        const currentPaddingTop = parseFloat(computedStyle.paddingTop) || 0
        const currentPaddingBottom =
          parseFloat(computedStyle.paddingBottom) || 0

        // Add padding to reach minimum size
        const additionalHorizontalPadding = widthDeficit / 2
        const additionalVerticalPadding = heightDeficit / 2

        element.style.paddingLeft = `${currentPaddingLeft + additionalHorizontalPadding}px`
        element.style.paddingRight = `${currentPaddingRight + additionalHorizontalPadding}px`
        element.style.paddingTop = `${currentPaddingTop + additionalVerticalPadding}px`
        element.style.paddingBottom = `${currentPaddingBottom + additionalVerticalPadding}px`

        // Verify the fix worked
        const newResult = checkTouchTarget(element)
        return newResult.passes
      }

      return false
    },
    [checkTouchTarget, minSize]
  )

  // Cleanup on unmount
  useEffect(() => {
    return () => {
      if (observerRef.current) {
        observerRef.current.disconnect()
      }
    }
  }, [])

  return {
    checkTouchTarget,
    validateAllTouchTargets,
    registerElement,
    unregisterElement,
    getCurrentViolations,
    recheckAll,
    autoFixTouchTarget,
    violations,
  }
}

/**
 * Hook for automatically monitoring touch targets in a container
 */
export function useAutoTouchTargetValidation(
  containerRef: React.RefObject<HTMLElement>,
  options: TouchTargetValidationOptions = {}
) {
  const touchTarget = useTouchTarget(options)
  const [isValidating, setIsValidating] = useState(false)

  useEffect(() => {
    if (!containerRef.current) return

    // Use setTimeout to avoid synchronous setState in effect
    const timeoutId = setTimeout(() => {
      setIsValidating(true)
      touchTarget.validateAllTouchTargets(containerRef.current!)
      setIsValidating(false)
    }, 0)

    // Register all interactive elements for monitoring
    const interactiveElements = containerRef.current.querySelectorAll(
      'button, a[href], input:not([type="hidden"]), select, textarea, [tabindex]:not([tabindex="-1"]), [role="button"], [role="link"], [onclick]'
    )

    interactiveElements.forEach(element => {
      touchTarget.registerElement(element as HTMLElement)
    })

    return () => {
      clearTimeout(timeoutId)
      interactiveElements.forEach(element => {
        touchTarget.unregisterElement(element as HTMLElement)
      })
    }
  }, [containerRef, touchTarget])

  return {
    ...touchTarget,
    isValidating,
  }
}

/**
 * Utility function to check if an element is interactive
 */
export function isInteractiveElement(element: HTMLElement): boolean {
  const interactiveTags = ['button', 'a', 'input', 'select', 'textarea']
  const interactiveRoles = ['button', 'link', 'menuitem', 'tab']

  const tagName = element.tagName.toLowerCase()
  const role = element.getAttribute('role')
  const tabIndex = element.getAttribute('tabindex')
  const hasClickHandler =
    element.onclick !== null || element.getAttribute('onclick') !== null

  return (
    interactiveTags.includes(tagName) ||
    (role && interactiveRoles.includes(role)) ||
    (tabIndex && tabIndex !== '-1') ||
    hasClickHandler
  )
}

/**
 * Get all interactive elements in a container
 */
export function getAllInteractiveElements(
  container: HTMLElement = document.body
): HTMLElement[] {
  const interactiveSelectors = [
    'button',
    'a[href]',
    'input:not([type="hidden"])',
    'select',
    'textarea',
    '[tabindex]:not([tabindex="-1"])',
    '[role="button"]',
    '[role="link"]',
    '[role="menuitem"]',
    '[role="tab"]',
    '[onclick]',
  ]

  const elements = container.querySelectorAll(interactiveSelectors.join(', '))
  return Array.from(elements).filter(element => {
    const htmlElement = element as HTMLElement
    const computedStyle = window.getComputedStyle(htmlElement)
    return (
      computedStyle.display !== 'none' && computedStyle.visibility !== 'hidden'
    )
  }) as HTMLElement[]
}

import React, { useState } from 'react'
import { useParams, useNavigate } from 'react-router-dom'
import { useDistricts } from '../hooks/useDistricts'
import { useDistrictAnalytics, ClubTrend } from '../hooks/useDistrictAnalytics'
import { useLeadershipInsights } from '../hooks/useLeadershipInsights'
import { useDistinguishedClubAnalytics } from '../hooks/useDistinguishedClubAnalytics'
import { useDistrictCachedDates } from '../hooks/useDistrictData'
import { useProgramYear } from '../contexts/ProgramYearContext'
import { ProgramYearSelector } from '../components/ProgramYearSelector'
import {
  getAvailableProgramYears,
  filterDatesByProgramYear,
  getMostRecentDateInProgramYear,
} from '../utils/programYear'
import { DistrictOverview } from '../components/DistrictOverview'
import { AtRiskClubsPanel } from '../components/AtRiskClubsPanel'
import { CriticalClubsPanel } from '../components/CriticalClubsPanel'
import { DistinguishedProgressChart } from '../components/DistinguishedProgressChart'
import { ClubsTable } from '../components/ClubsTable'
import { ClubDetailModal } from '../components/ClubDetailModal'
import { DivisionRankings } from '../components/DivisionRankings'
import { AreaPerformanceChart } from '../components/AreaPerformanceChart'
import { MembershipTrendChart } from '../components/MembershipTrendChart'
import { YearOverYearComparison } from '../components/YearOverYearComparison'
import { LeadershipInsights } from '../components/LeadershipInsights'
import { TopGrowthClubs } from '../components/TopGrowthClubs'
import { DCPGoalAnalysis } from '../components/DCPGoalAnalysis'

import ErrorBoundary from '../components/ErrorBoundary'
import { ErrorDisplay, EmptyState } from '../components/ErrorDisplay'
import { DistrictBackfillButton } from '../components/DistrictBackfillButton'
import { LazyChart } from '../components/LazyChart'
import { useBackfillContext } from '../contexts/BackfillContext'
import AssessmentPanel from '../components/AssessmentPanel'

type TabType =
  | 'overview'
  | 'clubs'
  | 'divisions'
  | 'trends'
  | 'analytics'
  | 'assessment'

const DistrictDetailPage: React.FC = () => {
  const { districtId } = useParams<{ districtId: string }>()
  const navigate = useNavigate()
  const [activeTab, setActiveTab] = useState<TabType>('overview')
  const [selectedClub, setSelectedClub] = useState<ClubTrend | null>(null)
  const { addBackfill } = useBackfillContext()

  // Use program year context
  const {
    selectedProgramYear,
    setSelectedProgramYear,
    selectedDate,
    setSelectedDate,
  } = useProgramYear()

  // Fetch district info
  const { data: districtsData } = useDistricts()
  const selectedDistrict = districtsData?.districts?.find(
    d => d.id === districtId
  )

  // Fetch cached dates for date selector
  const { data: cachedDatesData } = useDistrictCachedDates(districtId || '')

  // Get all cached dates
  const allCachedDates = React.useMemo(
    () => cachedDatesData?.dates || [],
    [cachedDatesData?.dates]
  )

  // Get available program years from cached dates
  const availableProgramYears = React.useMemo(() => {
    return getAvailableProgramYears(allCachedDates)
  }, [allCachedDates])

  // Filter cached dates by selected program year
  const cachedDatesInProgramYear = React.useMemo(() => {
    return filterDatesByProgramYear(allCachedDates, selectedProgramYear)
  }, [allCachedDates, selectedProgramYear])

  // Auto-select most recent date in program year when program year changes
  React.useEffect(() => {
    if (cachedDatesInProgramYear.length > 0 && !selectedDate) {
      const mostRecent = getMostRecentDateInProgramYear(
        allCachedDates,
        selectedProgramYear
      )
      if (mostRecent) {
        setSelectedDate(mostRecent)
      }
    }
  }, [
    selectedProgramYear,
    cachedDatesInProgramYear,
    allCachedDates,
    selectedDate,
    setSelectedDate,
  ])

  // Fetch analytics with program year boundaries
  const {
    data: analytics,
    isLoading: isLoadingAnalytics,
    error: analyticsError,
    refetch: refetchAnalytics,
  } = useDistrictAnalytics(
    districtId || null,
    selectedProgramYear.startDate,
    selectedDate || selectedProgramYear.endDate
  )

  // Fetch leadership insights for analytics tab - use program year boundaries
  const { data: leadershipInsights, isLoading: isLoadingLeadership } =
    useLeadershipInsights(
      districtId || null,
      selectedProgramYear.startDate,
      selectedDate || selectedProgramYear.endDate
    )

  // Fetch distinguished club analytics for analytics tab - use program year boundaries
  const { data: distinguishedAnalytics, isLoading: isLoadingDistinguished } =
    useDistinguishedClubAnalytics(
      districtId || null,
      selectedProgramYear.startDate,
      selectedDate || selectedProgramYear.endDate
    )

  const districtName = selectedDistrict?.name || 'Unknown District'

  // Get all clubs from analytics
  const allClubs = analytics?.allClubs || []

  // Separate critical and at-risk clubs - now they come as separate arrays
  const criticalClubs = React.useMemo(() => {
    return analytics?.criticalClubs || []
  }, [analytics?.criticalClubs])

  const atRiskClubs = React.useMemo(() => {
    return analytics?.atRiskClubs || []
  }, [analytics?.atRiskClubs])

  // Get available dates sorted in descending order (filtered by program year)
  const availableDates = cachedDatesInProgramYear.sort((a, b) =>
    b.localeCompare(a)
  )

  // Tab configuration
  const tabs: Array<{ id: TabType; label: string; disabled?: boolean }> = [
    { id: 'overview', label: 'Overview' },
    { id: 'clubs', label: 'Clubs' },
    { id: 'divisions', label: 'Divisions & Areas' },
    { id: 'trends', label: 'Trends' },
    { id: 'analytics', label: 'Analytics' },
    { id: 'assessment', label: 'Assessment' },
  ]

  // Handle club click
  const handleClubClick = (club: ClubTrend) => {
    setSelectedClub(club)
  }

  // Close modal
  const handleCloseModal = () => {
    setSelectedClub(null)
  }

  // Handle date selection
  const handleDateChange = (e: React.ChangeEvent<HTMLSelectElement>) => {
    const value = e.target.value
    setSelectedDate(value === 'latest' ? undefined : value)
  }

  // Format date for display
  const formatDate = (dateStr: string) => {
    const date = new Date(dateStr)
    return date.toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
    })
  }

  // Handle backfill start
  const handleBackfillStart = (backfillId: string) => {
    if (districtId) {
      addBackfill({
        backfillId,
        type: 'district',
        districtId,
      })
    }
  }

  return (
    <ErrorBoundary>
      <div className="min-h-screen bg-gray-100" id="main-content">
        <div className="container mx-auto px-4 py-4 sm:py-8">
          {/* Header */}
          <div className="mb-4 sm:mb-6">
            <button
              onClick={() => navigate('/')}
              className="flex items-center gap-2 text-tm-loyal-blue hover:text-tm-loyal-blue-80 font-tm-headline font-medium transition-colors mb-4"
            >
              <svg
                className="w-5 h-5"
                fill="none"
                stroke="currentColor"
                viewBox="0 0 24 24"
              >
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth={2}
                  d="M15 19l-7-7 7-7"
                />
              </svg>
              Back to Rankings
            </button>
            <div className="flex flex-col sm:flex-row sm:justify-between sm:items-start gap-4">
              <div>
                <h1 className="text-2xl sm:text-3xl font-tm-headline font-bold text-tm-black">
                  {districtName}
                </h1>
                <p className="text-sm sm:text-base font-tm-body text-gray-600 mt-1">
                  District Statistics & Performance Analytics
                </p>
              </div>

              {/* Program Year, Date Selector and Backfill Button */}
              <div className="flex flex-col sm:flex-row gap-3 items-stretch sm:items-start">
                {/* Program Year Selector */}
                {availableProgramYears.length > 0 && (
                  <div className="flex-shrink-0">
                    <ProgramYearSelector
                      availableProgramYears={availableProgramYears}
                      selectedProgramYear={selectedProgramYear}
                      onProgramYearChange={setSelectedProgramYear}
                      showProgress={true}
                    />
                  </div>
                )}

                {/* Date Selector - Shows only dates in selected program year */}
                {availableDates.length > 0 && (
                  <div className="flex flex-col gap-2">
                    <label
                      htmlFor="global-date-selector"
                      className="text-xs sm:text-sm font-tm-body font-medium text-gray-700"
                    >
                      View Specific Date
                    </label>
                    <select
                      id="global-date-selector"
                      value={selectedDate || 'latest'}
                      onChange={handleDateChange}
                      className="px-3 sm:px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-tm-loyal-blue focus:border-transparent bg-white text-gray-900 text-sm font-tm-body"
                      style={{ color: 'var(--tm-black)' }}
                    >
                      <option value="latest" className="text-gray-900 bg-white">
                        Latest in Program Year
                      </option>
                      {availableDates.map(date => (
                        <option
                          key={date}
                          value={date}
                          className="text-gray-900 bg-white"
                        >
                          {formatDate(date)}
                        </option>
                      ))}
                    </select>
                    <div className="text-xs font-tm-body text-gray-500">
                      {availableDates.length} date
                      {availableDates.length !== 1 ? 's' : ''} in program year
                    </div>
                  </div>
                )}

                {/* Backfill Button */}
                {districtId && (
                  <div className="flex flex-col gap-2">
                    <label className="text-xs sm:text-sm font-tm-body font-medium text-gray-700 opacity-0 pointer-events-none hidden sm:block">
                      Actions
                    </label>
                    <DistrictBackfillButton
                      districtId={districtId}
                      onBackfillStart={handleBackfillStart}
                    />
                  </div>
                )}
              </div>
            </div>
          </div>

          {/* Tabs */}
          <div className="bg-white rounded-lg shadow-md mb-4 sm:mb-6">
            <div className="border-b border-gray-200">
              <nav className="flex -mb-px overflow-x-auto scrollbar-hide">
                {tabs.map(tab => (
                  <button
                    key={tab.id}
                    onClick={() => !tab.disabled && setActiveTab(tab.id)}
                    disabled={tab.disabled}
                    className={`
                      px-4 sm:px-6 py-3 sm:py-4 text-xs sm:text-sm font-tm-headline font-medium whitespace-nowrap transition-colors
                      ${
                        activeTab === tab.id
                          ? 'border-b-2 border-tm-loyal-blue text-tm-loyal-blue'
                          : tab.disabled
                            ? 'text-gray-400 cursor-not-allowed'
                            : 'text-gray-600 hover:text-gray-900 hover:border-b-2 hover:border-tm-cool-gray'
                      }
                    `}
                  >
                    {tab.label}
                    {tab.disabled && (
                      <span className="ml-2 text-xs font-tm-body text-gray-400">
                        (Coming Soon)
                      </span>
                    )}
                  </button>
                ))}
              </nav>
            </div>
          </div>

          {/* Global Error State */}
          {analyticsError && activeTab === 'overview' && (
            <ErrorDisplay
              error={analyticsError}
              title="Failed to Load District Data"
              onRetry={() => refetchAnalytics()}
              showDetails={true}
            />
          )}

          {/* No Data Prompt with Backfill Button */}
          {!isLoadingAnalytics &&
            !analyticsError &&
            !analytics &&
            districtId && (
              <EmptyState
                title="No District Data Available"
                message="This district doesn't have any cached historical data yet. Start a backfill to collect performance data and unlock powerful analytics."
                icon="backfill"
                action={{
                  label: 'Start Backfill',
                  onClick: () => {
                    // This will be handled by the DistrictBackfillButton component
                    const backfillBtn = document.querySelector(
                      '[data-district-backfill]'
                    ) as HTMLButtonElement
                    if (backfillBtn) backfillBtn.click()
                  },
                }}
              />
            )}

          {/* Tab Content */}
          <div className="space-y-4 sm:space-y-6">
            {activeTab === 'overview' && districtId && analytics && (
              <>
                {/* District Overview - Now uses global date selector */}
                <DistrictOverview
                  districtId={districtId}
                  districtName={districtName}
                  {...(selectedDate && { selectedDate })}
                  programYearStartDate={selectedProgramYear.startDate}
                />

                {/* Critical Clubs Panel */}
                {analytics && (
                  <CriticalClubsPanel
                    clubs={criticalClubs}
                    isLoading={isLoadingAnalytics}
                  />
                )}

                {/* At-Risk Clubs Panel */}
                {analytics && (
                  <AtRiskClubsPanel
                    clubs={atRiskClubs}
                    isLoading={isLoadingAnalytics}
                  />
                )}

                {/* Distinguished Progress Chart - Lazy Loaded */}
                {analytics && (
                  <LazyChart height="300px">
                    <DistinguishedProgressChart
                      distinguishedClubs={analytics.distinguishedClubs}
                      distinguishedProjection={
                        analytics.distinguishedProjection
                      }
                      totalClubs={analytics.allClubs.length}
                      isLoading={isLoadingAnalytics}
                    />
                  </LazyChart>
                )}
              </>
            )}

            {activeTab === 'clubs' && districtId && (
              <>
                <ClubsTable
                  clubs={allClubs}
                  districtId={districtId}
                  isLoading={isLoadingAnalytics}
                  onClubClick={handleClubClick}
                />
                <ClubDetailModal
                  club={selectedClub}
                  onClose={handleCloseModal}
                />
              </>
            )}

            {activeTab === 'divisions' && (
              <>
                {/* Division Rankings */}
                {analytics && (
                  <DivisionRankings
                    divisions={analytics.divisionRankings}
                    isLoading={isLoadingAnalytics}
                  />
                )}

                {/* Area Performance Chart - Lazy Loaded */}
                {analytics && (
                  <LazyChart height="400px">
                    <AreaPerformanceChart
                      areas={analytics.topPerformingAreas}
                      isLoading={isLoadingAnalytics}
                    />
                  </LazyChart>
                )}
              </>
            )}

            {activeTab === 'trends' && (
              <>
                {/* Membership Trend Chart - Lazy Loaded */}
                {analytics && (
                  <LazyChart height="400px">
                    <MembershipTrendChart
                      membershipTrend={analytics.membershipTrend}
                      isLoading={isLoadingAnalytics}
                    />
                  </LazyChart>
                )}

                {/* Year-Over-Year Comparison - Lazy Loaded */}
                {analytics && (
                  <LazyChart height="300px">
                    <YearOverYearComparison
                      {...(analytics.yearOverYear && {
                        yearOverYear: analytics.yearOverYear,
                      })}
                      currentYear={{
                        totalMembership: analytics.totalMembership,
                        distinguishedClubs: analytics.distinguishedClubs.total,
                        healthyClubs: analytics.healthyClubs.length,
                        totalClubs: analytics.allClubs.length,
                      }}
                      isLoading={isLoadingAnalytics}
                    />
                  </LazyChart>
                )}
              </>
            )}

            {activeTab === 'analytics' && (
              <>
                {/* Leadership Insights */}
                <LeadershipInsights
                  insights={leadershipInsights || null}
                  isLoading={isLoadingLeadership}
                />

                {/* Top Growth Clubs */}
                {analytics && (
                  <TopGrowthClubs
                    topGrowthClubs={analytics.topGrowthClubs}
                    topDCPClubs={analytics.allClubs
                      .filter(club => club.dcpGoalsTrend.length > 0)
                      .map(club => ({
                        clubId: club.clubId,
                        clubName: club.clubName,
                        goalsAchieved:
                          club.dcpGoalsTrend[club.dcpGoalsTrend.length - 1]
                            ?.goalsAchieved || 0,
                        ...(club.distinguishedLevel &&
                          [
                            'Smedley',
                            'President',
                            'Select',
                            'Distinguished',
                          ].includes(club.distinguishedLevel) && {
                            distinguishedLevel: club.distinguishedLevel as
                              | 'Smedley'
                              | 'President'
                              | 'Select'
                              | 'Distinguished',
                          }),
                      }))
                      .sort((a, b) => b.goalsAchieved - a.goalsAchieved)
                      .slice(0, 10)}
                    isLoading={isLoadingAnalytics}
                  />
                )}

                {/* DCP Goal Analysis - Lazy Loaded */}
                {distinguishedAnalytics && (
                  <LazyChart height="400px">
                    <DCPGoalAnalysis
                      dcpGoalAnalysis={distinguishedAnalytics.dcpGoalAnalysis}
                      isLoading={isLoadingDistinguished}
                    />
                  </LazyChart>
                )}
              </>
            )}
          </div>
          {activeTab === 'assessment' && districtId && (
            <>
              <AssessmentPanel
                districtId={districtId}
                selectedProgramYear={selectedProgramYear}
                selectedDate={selectedDate}
              />
            </>
          )}
        </div>
      </div>
    </ErrorBoundary>
  )
}

export default DistrictDetailPage

import React, { useState } from 'react'
import { useNavigate } from 'react-router-dom'
import { useQuery } from '@tanstack/react-query'
import { apiClient } from '../services/api'
import HistoricalRankChart from '../components/HistoricalRankChart'
import { BackfillButton } from '../components/BackfillButton'
import { useBackfillContext } from '../contexts/BackfillContext'
import { useProgramYear } from '../contexts/ProgramYearContext'
import { ProgramYearSelector } from '../components/ProgramYearSelector'
import { useRankHistory } from '../hooks/useRankHistory'
import {
  getAvailableProgramYears,
  filterDatesByProgramYear,
  getMostRecentDateInProgramYear,
} from '../utils/programYear'
import { DistrictRanking } from '../types/districts'

const LandingPage: React.FC = () => {
  const navigate = useNavigate()
  const [sortBy, setSortBy] = useState<
    'aggregate' | 'clubs' | 'payments' | 'distinguished'
  >('aggregate')
  const [selectedRegions, setSelectedRegions] = useState<string[]>([])
  const [showClearConfirm, setShowClearConfirm] = useState(false)

  // Use global backfill context
  const { addBackfill } = useBackfillContext()

  // Use program year context
  const {
    selectedProgramYear,
    setSelectedProgramYear,
    selectedDate,
    setSelectedDate,
  } = useProgramYear()

  // Historical rank tracking state
  const [selectedRegionsForHistory, setSelectedRegionsForHistory] = useState<
    string[]
  >([])

  // Fetch cached dates
  const { data: cachedDatesData } = useQuery({
    queryKey: ['cached-dates'],
    queryFn: async () => {
      const response = await apiClient.get('/districts/cache/dates')
      return response.data
    },
  })

  const allCachedDates: string[] = React.useMemo(
    () => cachedDatesData?.dates || [],
    [cachedDatesData?.dates]
  )

  // Get available program years from cached dates
  const availableProgramYears = React.useMemo(() => {
    return getAvailableProgramYears(allCachedDates)
  }, [allCachedDates])

  // Filter cached dates by selected program year
  const cachedDates = React.useMemo(() => {
    return filterDatesByProgramYear(allCachedDates, selectedProgramYear)
  }, [allCachedDates, selectedProgramYear])

  // Auto-select most recent date in program year when program year changes
  React.useEffect(() => {
    if (cachedDates.length > 0 && !selectedDate) {
      const mostRecent = getMostRecentDateInProgramYear(
        allCachedDates,
        selectedProgramYear
      )
      if (mostRecent) {
        setSelectedDate(mostRecent)
      }
    }
  }, [
    selectedProgramYear,
    cachedDates,
    allCachedDates,
    selectedDate,
    setSelectedDate,
  ])

  // Fetch rankings for selected date
  const { data, isLoading, isError, error, refetch } = useQuery({
    queryKey: ['district-rankings', selectedDate],
    queryFn: async () => {
      const params = selectedDate ? { date: selectedDate } : {}
      const response = await apiClient.get('/districts/rankings', { params })
      return response.data
    },
    staleTime: 15 * 60 * 1000, // 15 minutes
  })

  const rankings: DistrictRanking[] = React.useMemo(
    () => data?.rankings || [],
    [data?.rankings]
  )
  const currentDate: string = data?.date || ''

  // Get district IDs for selected regions
  const selectedDistricts = React.useMemo(() => {
    if (selectedRegionsForHistory.length === 0) return []
    return rankings
      .filter(r => selectedRegionsForHistory.includes(r.region))
      .map(r => r.districtId)
  }, [rankings, selectedRegionsForHistory])

  // Fetch historical rank data for selected districts
  const {
    data: rankHistoryData,
    isLoading: isLoadingRankHistory,
    isError: isErrorRankHistory,
    error: rankHistoryError,
  } = useRankHistory({
    districtIds: selectedDistricts,
  })

  // Get unique regions for filter
  const regions = React.useMemo(() => {
    const uniqueRegions = new Set(rankings.map(r => r.region))
    return Array.from(uniqueRegions).sort()
  }, [rankings])

  // Initialize selected regions to all regions when data loads
  React.useEffect(() => {
    if (regions.length > 0 && selectedRegions.length === 0) {
      setSelectedRegions(regions)
    }
  }, [regions, selectedRegions.length])

  // Filter by selected regions
  const filteredRankings = React.useMemo(() => {
    if (selectedRegions.length === 0) {
      return rankings
    }
    return rankings.filter(r => selectedRegions.includes(r.region))
  }, [rankings, selectedRegions])

  const sortedRankings = React.useMemo(() => {
    const sorted = [...filteredRankings]
    switch (sortBy) {
      case 'clubs':
        return sorted.sort((a, b) => a.clubsRank - b.clubsRank) // Lower rank is better
      case 'payments':
        return sorted.sort((a, b) => a.paymentsRank - b.paymentsRank) // Lower rank is better
      case 'distinguished':
        return sorted.sort((a, b) => a.distinguishedRank - b.distinguishedRank) // Lower rank is better
      default:
        return sorted.sort((a, b) => b.aggregateScore - a.aggregateScore) // Higher Borda score is better
    }
  }, [filteredRankings, sortBy])

  const handleDistrictClick = (districtId: string) => {
    navigate(`/district/${districtId}`)
  }

  const getRankBadgeColor = (rank: number) => {
    if (rank === 1) return 'bg-yellow-500 text-white'
    if (rank === 2) return 'bg-gray-400 text-white'
    if (rank === 3) return 'bg-amber-600 text-white'
    if (rank <= 10) return 'bg-tm-loyal-blue text-white'
    return 'bg-gray-200 text-gray-700'
  }

  const formatNumber = (num: number) => {
    return num.toLocaleString()
  }

  const formatPercentage = (
    percent: number
  ): { text: string; color: string } => {
    if (percent > 0) {
      return {
        text: `+${percent.toFixed(1)}%`,
        color: 'text-green-600',
      }
    } else if (percent < 0) {
      return {
        text: `${percent.toFixed(1)}%`,
        color: 'text-red-600',
      }
    } else {
      return {
        text: '0.0%',
        color: 'text-gray-600',
      }
    }
  }

  // Handle region selection for historical tracking
  const handleRegionSelection = (region: string) => {
    setSelectedRegionsForHistory(prev => {
      if (prev.includes(region)) {
        return prev.filter(r => r !== region)
      } else {
        return [...prev, region]
      }
    })
  }

  if (isLoading) {
    return (
      <div className="min-h-screen bg-gray-100" id="main-content">
        <div className="container mx-auto px-4 py-8">
          <div className="bg-white rounded-lg shadow-md p-8">
            <div className="animate-pulse space-y-4">
              <div className="h-8 bg-gray-200 rounded w-1/3"></div>
              <div className="h-4 bg-gray-200 rounded w-2/3"></div>
              <div className="space-y-3 mt-8">
                {[...Array(10)].map((_, i) => (
                  <div key={i} className="h-20 bg-gray-200 rounded"></div>
                ))}
              </div>
            </div>
          </div>
        </div>
      </div>
    )
  }

  if (isError) {
    // Check if this is a "no snapshots available" error
    const errorResponse = (
      error as Error & {
        response?: {
          data?: {
            error?: {
              code?: string
            }
          }
        }
      }
    )?.response?.data?.error
    const isNoSnapshotError = errorResponse?.code === 'NO_SNAPSHOT_AVAILABLE'

    if (isNoSnapshotError) {
      return (
        <div className="min-h-screen bg-gray-100" id="main-content">
          <div className="container mx-auto px-4 py-8">
            <div className="bg-tm-happy-yellow bg-opacity-20 border border-tm-happy-yellow rounded-lg p-8 max-w-2xl mx-auto">
              <div className="text-center">
                <div className="w-16 h-16 mx-auto mb-4 bg-tm-loyal-blue rounded-full flex items-center justify-center">
                  <svg
                    className="w-8 h-8 text-white"
                    fill="none"
                    stroke="currentColor"
                    viewBox="0 0 24 24"
                    aria-hidden="true"
                  >
                    <path
                      strokeLinecap="round"
                      strokeLinejoin="round"
                      strokeWidth={2}
                      d="M9 12h6m-6 4h6m2 5H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z"
                    />
                  </svg>
                </div>
                <h2 className="text-2xl font-bold text-tm-black mb-3">
                  Welcome to Toast-Stats!
                </h2>
                <p className="text-tm-black mb-6 text-lg">
                  No data snapshots are available yet. To get started, you'll
                  need to fetch data from the Toastmasters dashboard.
                </p>

                <div className="bg-white rounded-lg p-6 mb-6 text-left">
                  <h3 className="font-semibold text-tm-black mb-3">
                    What happens next:
                  </h3>
                  <ul className="space-y-2 text-tm-black">
                    <li className="flex items-start">
                      <span className="text-tm-loyal-blue mr-2">1.</span>
                      Click "Backfill Data" to fetch current and historical data
                    </li>
                    <li className="flex items-start">
                      <span className="text-tm-loyal-blue mr-2">2.</span>
                      The system will download data from the Toastmasters
                      dashboard
                    </li>
                    <li className="flex items-start">
                      <span className="text-tm-loyal-blue mr-2">3.</span>
                      Once complete, district rankings and analytics will be
                      available
                    </li>
                  </ul>
                </div>

                <div className="flex flex-col sm:flex-row gap-4 justify-center">
                  <BackfillButton
                    className="px-6 py-3 text-lg"
                    onBackfillStart={() => {
                      // Optionally show a success message or redirect
                    }}
                  />
                  <button
                    onClick={() => refetch()}
                    className="px-6 py-3 bg-tm-cool-gray text-tm-black rounded-lg hover:bg-opacity-80 transition-colors"
                  >
                    Check Again
                  </button>
                </div>

                <p className="text-sm text-tm-cool-gray mt-4">
                  This is a one-time setup. Future visits will show your data
                  immediately.
                </p>
              </div>
            </div>
          </div>
        </div>
      )
    }

    // Handle other types of errors
    return (
      <div className="min-h-screen bg-gray-100" id="main-content">
        <div className="container mx-auto px-4 py-8">
          <div className="bg-red-50 border border-red-200 rounded-lg p-6">
            <h2 className="text-xl font-bold text-red-800 mb-2">
              Error Loading Rankings
            </h2>
            <p className="text-red-600">
              {(error as Error)?.message || 'Failed to load district rankings'}
            </p>
            <button
              onClick={() => refetch()}
              className="mt-4 px-4 py-2 bg-red-600 text-white rounded hover:bg-red-700 transition-colors"
            >
              Try Again
            </button>
          </div>
        </div>
      </div>
    )
  }

  return (
    <div className="min-h-screen bg-gray-100" id="main-content">
      <div className="container mx-auto px-4 py-8">
        {/* Header */}
        <div className="bg-white rounded-lg shadow-md p-6 mb-6">
          <div className="flex flex-col sm:flex-row sm:justify-between sm:items-start gap-4 mb-4">
            <div>
              <h1 className="text-3xl font-bold text-gray-900 mb-2">
                Toastmasters District Rankings
              </h1>
              <p className="text-gray-600">
                Compare district performance across paid clubs, payments, and
                distinguished clubs
              </p>
            </div>
            <div className="flex gap-3">
              <BackfillButton
                className="text-sm font-medium"
                onBackfillStart={id =>
                  addBackfill({ backfillId: id, type: 'global' })
                }
              />
              <button
                onClick={() => setShowClearConfirm(true)}
                className="px-4 py-2 bg-red-600 text-white rounded-lg hover:bg-red-700 transition-colors text-sm font-medium"
              >
                Clear Cache
              </button>
            </div>
          </div>

          {/* Program Year and Date Selectors */}
          <div className="flex flex-col sm:flex-row gap-4 pt-4 border-t border-gray-200">
            {/* Program Year Selector */}
            {availableProgramYears.length > 0 && (
              <div className="flex-shrink-0">
                <ProgramYearSelector
                  availableProgramYears={availableProgramYears}
                  selectedProgramYear={selectedProgramYear}
                  onProgramYearChange={setSelectedProgramYear}
                  showProgress={true}
                />
              </div>
            )}

            {/* Date Selector - Shows only dates in selected program year */}
            {cachedDates.length > 0 && (
              <div className="flex flex-col gap-2 flex-1">
                <label
                  htmlFor="date-select"
                  className="text-xs sm:text-sm font-medium text-gray-700"
                >
                  View Specific Date
                </label>
                <select
                  id="date-select"
                  value={selectedDate || ''}
                  onChange={e => setSelectedDate(e.target.value || undefined)}
                  className="px-4 py-2 border border-gray-300 rounded-lg font-medium text-gray-900 hover:border-tm-loyal-blue-50 focus:outline-none focus:ring-2 focus:ring-tm-loyal-blue transition-colors bg-white font-tm-body"
                  style={{ color: 'var(--tm-black)' }}
                >
                  <option value="" className="text-gray-900 bg-white">
                    Latest in Program Year ({currentDate})
                  </option>
                  {cachedDates
                    .sort((a, b) => b.localeCompare(a))
                    .map(date => (
                      <option
                        key={date}
                        value={date}
                        className="text-gray-900 bg-white"
                      >
                        {new Date(date).toLocaleDateString('en-US', {
                          year: 'numeric',
                          month: 'short',
                          day: 'numeric',
                        })}
                      </option>
                    ))}
                </select>
                <div className="text-xs text-gray-500">
                  {cachedDates.length} date{cachedDates.length !== 1 ? 's' : ''}{' '}
                  available in this program year
                </div>
              </div>
            )}
          </div>
        </div>

        {/* Clear Cache Confirmation Dialog */}
        {showClearConfirm && (
          <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
            <div className="bg-white rounded-lg shadow-xl max-w-md w-full p-6">
              <h3 className="text-lg font-bold text-gray-900 mb-2">
                Clear Cache?
              </h3>
              <p className="text-gray-600 mb-6">
                This will delete all cached district data. The next data fetch
                will download fresh data from the Toastmasters dashboard.
              </p>
              <div className="flex gap-3 justify-end">
                <button
                  onClick={() => setShowClearConfirm(false)}
                  className="px-4 py-2 bg-gray-200 text-gray-700 rounded-lg hover:bg-gray-300 transition-colors font-medium"
                >
                  Cancel
                </button>
                <button
                  onClick={async () => {
                    try {
                      await apiClient.delete('/districts/cache')
                      setShowClearConfirm(false)
                      refetch()
                    } catch (err) {
                      console.error('Failed to clear cache:', err)
                    }
                  }}
                  className="px-4 py-2 bg-red-600 text-white rounded-lg hover:bg-red-700 transition-colors font-medium"
                >
                  Clear Cache
                </button>
              </div>
            </div>
          </div>
