import React, { useState, FormEvent } from 'react'
import { useNavigate } from 'react-router-dom'
import { useAuth } from '../hooks/useAuth'

const LoginPage: React.FC = () => {
  const [username, setUsername] = useState('')
  const [password, setPassword] = useState('')
  const [error, setError] = useState('')
  const [isLoading, setIsLoading] = useState(false)
  const [validationErrors, setValidationErrors] = useState<{
    username?: string
    password?: string
  }>({})

  const { login } = useAuth()
  const navigate = useNavigate()

  const validateForm = (): boolean => {
    const errors: { username?: string; password?: string } = {}

    if (!username.trim()) {
      errors.username = 'Username is required'
    }

    if (!password.trim()) {
      errors.password = 'Password is required'
    }

    setValidationErrors(errors)
    return Object.keys(errors).length === 0
  }

  const handleSubmit = async (e: FormEvent<HTMLFormElement>) => {
    e.preventDefault()
    setError('')
    setValidationErrors({})

    // Validate form
    if (!validateForm()) {
      return
    }

    setIsLoading(true)

    try {
      await login(username, password)
      // Redirect to dashboard on successful login
      navigate('/dashboard')
    } catch (err: unknown) {
      // Display error message for authentication failures
      const error = err as {
        response?: { status?: number; data?: { message?: string } }
      }
      if (error.response?.status === 401) {
        setError('Invalid username or password')
      } else if (error.response?.data?.message) {
        setError(error.response.data.message)
      } else if (error instanceof Error && error.message) {
        setError(error.message)
      } else {
        setError('An error occurred during login. Please try again.')
      }
    } finally {
      setIsLoading(false)
    }
  }

  return (
    <div className="min-h-screen bg-gray-100 flex items-center justify-center px-4">
      <main
        className="bg-white p-8 rounded-lg shadow-md w-full max-w-md"
        id="main-content"
      >
        <h1 className="text-2xl font-bold text-gray-900 mb-6 text-center">
          Toastmasters District Visualizer
        </h1>
        <h2 className="text-xl font-semibold text-gray-700 mb-6 text-center">
          Login
        </h2>

        <form onSubmit={handleSubmit} noValidate aria-label="Login form">
          {/* General error message */}
          {error && (
            <div
              className="mb-4 p-3 bg-red-100 border border-red-400 text-red-700 rounded"
              role="alert"
            >
              {error}
            </div>
          )}

          {/* Username field */}
          <div className="mb-4">
            <label
              htmlFor="username"
              className="block text-gray-700 font-medium mb-2"
            >
              Username
            </label>
            <input
              type="text"
              id="username"
              value={username}
              onChange={e => setUsername(e.target.value)}
              className={`w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-tm-loyal-blue text-gray-900 bg-white font-tm-body ${
                validationErrors.username ? 'border-red-500' : 'border-gray-300'
              }`}
              disabled={isLoading}
              aria-invalid={!!validationErrors.username}
              aria-describedby={
                validationErrors.username ? 'username-error' : undefined
              }
            />
            {validationErrors.username && (
              <p
                id="username-error"
                className="mt-1 text-sm text-red-600"
                role="alert"
              >
                {validationErrors.username}
              </p>
            )}
          </div>

          {/* Password field */}
          <div className="mb-6">
            <label
              htmlFor="password"
              className="block text-gray-700 font-medium mb-2"
            >
              Password
            </label>
            <input
              type="password"
              id="password"
              value={password}
              onChange={e => setPassword(e.target.value)}
              className={`w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-tm-loyal-blue text-gray-900 bg-white font-tm-body ${
                validationErrors.password ? 'border-red-500' : 'border-gray-300'
              }`}
              disabled={isLoading}
              aria-invalid={!!validationErrors.password}
              aria-describedby={
                validationErrors.password ? 'password-error' : undefined
              }
            />
            {validationErrors.password && (
              <p
                id="password-error"
                className="mt-1 text-sm text-red-600"
                role="alert"
              >
                {validationErrors.password}
              </p>
            )}
          </div>

          {/* Submit button */}
          <button
            type="submit"
            disabled={isLoading}
            className={`w-full py-2 px-4 rounded-lg font-medium text-white transition-colors font-tm-body ${
              isLoading
                ? 'bg-tm-loyal-blue-50 cursor-not-allowed'
                : 'bg-tm-loyal-blue hover:bg-tm-loyal-blue-80'
            }`}
          >
            {isLoading ? (
              <span className="flex items-center justify-center">
                <svg
                  className="animate-spin -ml-1 mr-3 h-5 w-5 text-white"
                  xmlns="http://www.w3.org/2000/svg"
                  fill="none"
                  viewBox="0 0 24 24"
                  aria-hidden="true"
                >
                  <circle
                    className="opacity-25"
                    cx="12"
                    cy="12"
                    r="10"
                    stroke="currentColor"
                    strokeWidth="4"
                  ></circle>
                  <path
                    className="opacity-75"
                    fill="currentColor"
                    d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"
                  ></path>
                </svg>
                Logging in...
              </span>
            ) : (
              'Login'
            )}
          </button>
        </form>
      </main>
    </div>
  )
}

export default LoginPage

/**
 * WCAG AA Contrast Validation Utilities
 *
 * This module provides comprehensive contrast validation utilities for ensuring
 * WCAG AA compliance (4.5:1 ratio for normal text, 3:1 for large text).
 *
 * Requirements: 3.1 - Accessibility Compliance
 */

export interface ContrastValidationResult {
  ratio: number
  passes: boolean
  level: 'AA' | 'AAA' | 'fail'
  foreground: string
  background: string
  isLargeText?: boolean
  recommendation?: string
}

export interface ColorRGB {
  r: number
  g: number
  b: number
}

/**
 * Convert hex color to RGB values
 */
export function hexToRgb(hex: string): ColorRGB | null {
  // Remove # if present and handle 3-digit hex
  const cleanHex = hex.replace('#', '')

  if (cleanHex.length === 3) {
    const result = /^([a-f\d])([a-f\d])([a-f\d])$/i.exec(cleanHex)
    return result && result[1] && result[2] && result[3]
      ? {
          r: parseInt(result[1] + result[1], 16),
          g: parseInt(result[2] + result[2], 16),
          b: parseInt(result[3] + result[3], 16),
        }
      : null
  }

  const result = /^([a-f\d]{2})([a-f\d]{2})([a-f\d]{2})$/i.exec(cleanHex)
  return result && result[1] && result[2] && result[3]
    ? {
        r: parseInt(result[1], 16),
        g: parseInt(result[2], 16),
        b: parseInt(result[3], 16),
      }
    : null
}

/**
 * Convert RGB color to hex
 */
export function rgbToHex(r: number, g: number, b: number): string {
  const toHex = (n: number) => {
    const hex = Math.round(Math.max(0, Math.min(255, n))).toString(16)
    return hex.length === 1 ? '0' + hex : hex
  }
  return `#${toHex(r)}${toHex(g)}${toHex(b)}`
}

/**
 * Calculate relative luminance of a color according to WCAG 2.1
 * https://www.w3.org/WAI/WCAG21/Understanding/contrast-minimum.html
 */
export function getRelativeLuminance(r: number, g: number, b: number): number {
  const [rs, gs, bs] = [r, g, b].map(c => {
    c = c / 255
    return c <= 0.03928 ? c / 12.92 : Math.pow((c + 0.055) / 1.055, 2.4)
  })
  return 0.2126 * (rs ?? 0) + 0.7152 * (gs ?? 0) + 0.0722 * (bs ?? 0)
}

/**
 * Calculate contrast ratio between two colors
 * Returns a value between 1 and 21 (21:1 is perfect contrast)
 */
export function calculateContrastRatio(
  foreground: string,
  background: string
): number {
  const fgRgb = hexToRgb(foreground)
  const bgRgb = hexToRgb(background)

  if (!fgRgb || !bgRgb) {
    console.warn(`Invalid color format: ${foreground} or ${background}`)
    return 1 // Worst case if colors can't be parsed
  }

  const fgLuminance = getRelativeLuminance(fgRgb.r, fgRgb.g, fgRgb.b)
  const bgLuminance = getRelativeLuminance(bgRgb.r, bgRgb.g, bgRgb.b)

  const lighter = Math.max(fgLuminance, bgLuminance)
  const darker = Math.min(fgLuminance, bgLuminance)

  return (lighter + 0.05) / (darker + 0.05)
}

/**
 * Validate contrast ratio against WCAG AA standards
 * @param foreground - Foreground color (hex)
 * @param background - Background color (hex)
 * @param isLargeText - Whether text is large (18pt+ or 14pt+ bold)
 */
export function validateContrastRatio(
  foreground: string,
  background: string,
  isLargeText = false
): ContrastValidationResult {
  const ratio = calculateContrastRatio(foreground, background)
  const requiredRatio = isLargeText ? 3.0 : 4.5
  const aaaRatio = isLargeText ? 4.5 : 7.0

  let level: 'AA' | 'AAA' | 'fail'
  let recommendation: string | undefined

  if (ratio >= aaaRatio) {
    level = 'AAA'
  } else if (ratio >= requiredRatio) {
    level = 'AA'
  } else {
    level = 'fail'
    recommendation = `Contrast ratio ${ratio.toFixed(2)}:1 is below WCAG AA requirement of ${requiredRatio}:1. Consider using a darker foreground or lighter background.`
  }

  return {
    ratio: Math.round(ratio * 100) / 100,
    passes: ratio >= requiredRatio,
    level,
    foreground,
    background,
    isLargeText,
    ...(recommendation && { recommendation }),
  }
}

/**
 * Get all text/background color combinations from DOM elements
 */
export function getAllColorCombinations(
  container: HTMLElement = document.body
): Array<{ foreground: string; background: string; element: HTMLElement }> {
  const combinations: Array<{
    foreground: string
    background: string
    element: HTMLElement
  }> = []
  const elements = container.querySelectorAll('*')

  elements.forEach(element => {
    const htmlElement = element as HTMLElement
    const computedStyle = window.getComputedStyle(htmlElement)
    const color = computedStyle.color
    const backgroundColor = computedStyle.backgroundColor

    // Convert RGB to hex if needed
    const foreground = rgbStringToHex(color)
    const background =
      rgbStringToHex(backgroundColor) || getBackgroundColor(htmlElement)

    if (foreground && background && foreground !== background) {
      combinations.push({
        foreground,
        background,
        element: htmlElement,
      })
    }
  })

  return combinations
}

/**
 * Convert RGB string to hex
 */
function rgbStringToHex(rgbString: string): string | null {
  if (rgbString.startsWith('#')) {
    return rgbString
  }

  const rgbMatch = rgbString.match(/rgb\((\d+),\s*(\d+),\s*(\d+)\)/)
  if (rgbMatch && rgbMatch[1] && rgbMatch[2] && rgbMatch[3]) {
    const r = parseInt(rgbMatch[1])
    const g = parseInt(rgbMatch[2])
    const b = parseInt(rgbMatch[3])
    return rgbToHex(r, g, b)
  }

  const rgbaMatch = rgbString.match(/rgba\((\d+),\s*(\d+),\s*(\d+),\s*[\d.]+\)/)
  if (rgbaMatch && rgbaMatch[1] && rgbaMatch[2] && rgbaMatch[3]) {
    const r = parseInt(rgbaMatch[1])
    const g = parseInt(rgbaMatch[2])
    const b = parseInt(rgbaMatch[3])
    return rgbToHex(r, g, b)
  }

  return null
}

/**
 * Get effective background color by traversing up the DOM tree
 */
function getBackgroundColor(element: HTMLElement): string {
  let current: HTMLElement | null = element

  while (current && current !== document.body) {
    const computedStyle = window.getComputedStyle(current)
    const backgroundColor = computedStyle.backgroundColor

    if (
      backgroundColor &&
      backgroundColor !== 'rgba(0, 0, 0, 0)' &&
      backgroundColor !== 'transparent'
    ) {
      const hex = rgbStringToHex(backgroundColor)
      if (hex) return hex
    }

    current = current.parentElement
  }

  return '#ffffff' // Default to white background
}

/**
 * Validate all text/background combinations in a container
 */
export function validateAllContrasts(
  container: HTMLElement = document.body
): ContrastValidationResult[] {
  const combinations = getAllColorCombinations(container)

  return combinations.map(({ foreground, background, element }) => {
    const isLargeText = isElementLargeText(element)
    return validateContrastRatio(foreground, background, isLargeText)
  })
}

/**
 * Check if an element contains large text (18pt+ or 14pt+ bold)
 */
function isElementLargeText(element: HTMLElement): boolean {
  const computedStyle = window.getComputedStyle(element)
  const fontSize = parseFloat(computedStyle.fontSize)
  const fontWeight = computedStyle.fontWeight

  // Convert to points (1pt = 1.33px approximately)
  const fontSizeInPoints = fontSize * 0.75

  // Large text is 18pt+ or 14pt+ bold
  return (
    fontSizeInPoints >= 18 ||
    (fontSizeInPoints >= 14 &&
      (fontWeight === 'bold' ||
        fontWeight === '700' ||
        fontWeight === '800' ||
        fontWeight === '900' ||
        parseInt(fontWeight) >= 700))
  )
}

/**
 * Find the best contrasting color from a set of options
 */
export function findBestContrastingColor(
  background: string,
  colorOptions: string[],
  isLargeText = false
): { color: string; ratio: number } | null {
  let bestColor: string | null = null
  let bestRatio = 0

  for (const color of colorOptions) {
    const ratio = calculateContrastRatio(color, background)
    if (ratio > bestRatio) {
      bestRatio = ratio
      bestColor = color
    }
  }

  if (!bestColor) return null

  const requiredRatio = isLargeText ? 3.0 : 4.5
  return bestRatio >= requiredRatio
    ? { color: bestColor, ratio: bestRatio }
    : null
}

/**
 * Generate a high-contrast version of a color
 */
export function generateHighContrastColor(
  background: string,
  preferDark = true
): string {
  const bgRgb = hexToRgb(background)
  if (!bgRgb) return preferDark ? '#000000' : '#ffffff'

  const bgLuminance = getRelativeLuminance(bgRgb.r, bgRgb.g, bgRgb.b)

  // If background is light, return dark color; if dark, return light color
  return bgLuminance > 0.5 ? '#000000' : '#ffffff'
}

/**
 * Batch validate multiple color combinations
 */
export function batchValidateContrasts(
  combinations: Array<{
    foreground: string
    background: string
    isLargeText?: boolean
  }>
): ContrastValidationResult[] {
  return combinations.map(({ foreground, background, isLargeText = false }) =>
    validateContrastRatio(foreground, background, isLargeText)
  )
}

/**
 * Semantic Markup Validation Utilities
 * Requirements: 3.5 - Semantic markup and proper heading hierarchy
 */

export interface SemanticValidationResult {
  element: HTMLElement
  issues: string[]
  passes: boolean
  recommendations: string[]
}

export interface HeadingHierarchyResult {
  headings: Array<{
    element: HTMLElement
    level: number
    text: string
    hasSkippedLevel: boolean
  }>
  violations: Array<{
    element: HTMLElement
    issue: string
    recommendation: string
  }>
  passes: boolean
}

/**
 * Validate semantic markup for interactive elements
 */
export function validateSemanticMarkup(
  element: HTMLElement
): SemanticValidationResult {
  const issues: string[] = []
  const recommendations: string[] = []

  // Check for proper ARIA labels
  if (isInteractiveElement(element)) {
    const ariaLabel = element.getAttribute('aria-label')
    const ariaLabelledBy = element.getAttribute('aria-labelledby')
    const textContent = element.textContent?.trim()

    // Interactive elements should have accessible names
    if (!ariaLabel && !ariaLabelledBy && !textContent) {
      issues.push('Interactive element lacks accessible name')
      recommendations.push(
        'Add aria-label, aria-labelledby, or visible text content'
      )
    }

    // Check for proper roles
    const role = element.getAttribute('role')
    const tagName = element.tagName.toLowerCase()

    if (tagName === 'div' && element.onclick && !role) {
      issues.push('Clickable div lacks proper role')
      recommendations.push('Add role="button" or use a proper button element')
    }

    // Check for keyboard accessibility
    const tabIndex = element.getAttribute('tabindex')
    if (
      element.onclick &&
      !['button', 'a', 'input', 'select', 'textarea'].includes(tagName) &&
      tabIndex === null
    ) {
      issues.push('Interactive element not keyboard accessible')
      recommendations.push('Add tabindex="0" or use a focusable element')
    }
  }

  // Check for proper form labels
  if (
    element.tagName.toLowerCase() === 'input' &&
    element.getAttribute('type') !== 'hidden'
  ) {
    const id = element.getAttribute('id')
    const ariaLabel = element.getAttribute('aria-label')
    const ariaLabelledBy = element.getAttribute('aria-labelledby')

    let hasLabel = false
    if (id) {
      const label = document.querySelector(`label[for="${id}"]`)
      hasLabel = !!label
    }

    if (!hasLabel && !ariaLabel && !ariaLabelledBy) {
      issues.push('Form input lacks proper label')
      recommendations.push(
        'Add a label element with for attribute, aria-label, or aria-labelledby'
      )
    }
  }

  // Check for proper heading structure
  if (element.tagName.match(/^H[1-6]$/)) {
    const textContent = element.textContent?.trim()

    if (!textContent) {
      issues.push('Heading element is empty')
      recommendations.push('Add descriptive text content to the heading')
    }
  }

  // Check for proper list structure
  if (element.tagName.toLowerCase() === 'li') {
    const parent = element.parentElement
    if (!parent || !['ul', 'ol'].includes(parent.tagName.toLowerCase())) {
      issues.push('List item not contained in proper list element')
      recommendations.push('Wrap list items in ul or ol elements')
    }
  }

  return {
    element,
    issues,
    passes: issues.length === 0,
    recommendations,
  }
}

/**
 * Validate heading hierarchy in a container
 */
export function validateHeadingHierarchy(
  container: HTMLElement = document.body
): HeadingHierarchyResult {
  const headingElements = container.querySelectorAll('h1, h2, h3, h4, h5, h6')
  const headings: HeadingHierarchyResult['headings'] = []
  const violations: HeadingHierarchyResult['violations'] = []

  let previousLevel = 0

  headingElements.forEach(element => {
    const htmlElement = element as HTMLElement
    const level = parseInt(htmlElement.tagName.charAt(1))
    const text = htmlElement.textContent?.trim() || ''

    // Check for skipped levels
    const hasSkippedLevel = level > previousLevel + 1 && previousLevel > 0

    if (hasSkippedLevel) {
      violations.push({
        element: htmlElement,
        issue: `Heading level ${level} follows h${previousLevel}, skipping intermediate levels`,
        recommendation: `Use h${previousLevel + 1} instead of h${level}, or add intermediate heading levels`,
      })
    }

    // Check for empty headings
    if (!text) {
      violations.push({
        element: htmlElement,
        issue: 'Heading element is empty',
        recommendation: 'Add descriptive text content to the heading',
      })
    }

    headings.push({
      element: htmlElement,
      level,
      text,
      hasSkippedLevel,
    })

    previousLevel = level
  })

  // Check for missing h1
  const hasH1 = headings.some(h => h.level === 1)
  if (headings.length > 0 && !hasH1) {
    violations.push({
      element: container,
      issue: 'Page lacks h1 heading',
      recommendation: 'Add an h1 element as the main page heading',
    })
  }

  return {
    headings,
    violations,
    passes: violations.length === 0,
  }
}

/**
 * Validate all semantic markup in a container
 */
export function validateAllSemanticMarkup(
  container: HTMLElement = document.body
): SemanticValidationResult[] {
  const elements = container.querySelectorAll('*')
  const results: SemanticValidationResult[] = []

  elements.forEach(element => {
    const htmlElement = element as HTMLElement
    const result = validateSemanticMarkup(htmlElement)

    if (!result.passes) {
      results.push(result)
    }
  })

  return results
}

/**
 * Check if an element is interactive
 */
function isInteractiveElement(element: HTMLElement): boolean {
  const interactiveTags = ['button', 'a', 'input', 'select', 'textarea']
  const interactiveRoles = [
    'button',
    'link',
    'menuitem',
    'tab',
    'checkbox',
    'radio',
  ]

  const tagName = element.tagName.toLowerCase()
  const role = element.getAttribute('role')
  const tabIndex = element.getAttribute('tabindex')
  const hasClickHandler =
    element.onclick !== null || element.getAttribute('onclick') !== null

  return (
    interactiveTags.includes(tagName) ||
    (role && interactiveRoles.includes(role)) ||
    (tabIndex && tabIndex !== '-1') ||
    hasClickHandler
  )
}

/**
 * Generate accessibility report for a container
 */
export function generateAccessibilityReport(
  container: HTMLElement = document.body
): {
  contrastViolations: ContrastValidationResult[]
  semanticIssues: SemanticValidationResult[]
  headingHierarchy: HeadingHierarchyResult
  summary: {
    totalIssues: number
    criticalIssues: number
    passesBasicAccessibility: boolean
  }
} {
  const contrastViolations = validateAllContrasts(container).filter(
    r => !r.passes
  )
  const semanticIssues = validateAllSemanticMarkup(container)
  const headingHierarchy = validateHeadingHierarchy(container)

  const totalIssues =
    contrastViolations.length +
    semanticIssues.length +
    headingHierarchy.violations.length
  const criticalIssues =
    contrastViolations.filter(v => v.level === 'fail').length +
    semanticIssues.filter(s =>
      s.issues.some(i => i.includes('accessible name'))
    ).length

  return {
    contrastViolations,
    semanticIssues,
    headingHierarchy,
    summary: {
      totalIssues,
      criticalIssues,
      passesBasicAccessibility: totalIssues === 0,
    },
  }
}

/**
 * Performance Monitoring Utilities
 *
 * Specialized monitoring for font loading, CSS bundle size, and brand-related performance metrics.
 */

export interface FontLoadingMetrics {
  fontFamily: string
  loadTime: number
  size: number
  status: 'loading' | 'loaded' | 'error' | 'timeout'
  timestamp: number
}

export interface CSSBundleMetrics {
  totalSize: number
  brandRelatedSize: number
  compressionRatio: number
  loadTime: number
  cacheHitRate: number
  timestamp: number
}

export interface ValidationPerformanceMetrics {
  totalValidationTime: number
  rulesExecuted: number
  elementsValidated: number
  averageTimePerElement: number
  averageTimePerRule: number
  timestamp: number
}

export interface PerformanceAlert {
  type:
    | 'font-load-slow'
    | 'bundle-size-large'
    | 'validation-slow'
    | 'memory-high'
  severity: 'low' | 'medium' | 'high' | 'critical'
  message: string
  data:
    | FontLoadingMetrics
    | CSSBundleMetrics
    | ValidationPerformanceMetrics
    | Record<string, unknown>
  timestamp: number
}

class PerformanceMonitoringService {
  private fontMetrics: FontLoadingMetrics[] = []
  private cssMetrics: CSSBundleMetrics[] = []
  private validationMetrics: ValidationPerformanceMetrics[] = []
  private alerts: PerformanceAlert[] = []
  private observers: PerformanceObserver[] = []

  constructor() {
    this.initialize()
  }

  private initialize(): void {
    if (typeof window !== 'undefined' && 'PerformanceObserver' in window) {
      this.setupFontLoadingMonitoring()
      this.setupResourceMonitoring()
      this.setupMemoryMonitoring()
    }
  }

  private setupFontLoadingMonitoring(): void {
    try {
      const observer = new PerformanceObserver(list => {
        const entries = list.getEntries()
        entries.forEach(entry => {
          if (entry.entryType === 'resource' && entry.name.includes('font')) {
            this.processFontLoadingEntry(entry as PerformanceResourceTiming)
          }
        })
      })

      observer.observe({ entryTypes: ['resource'] })
      this.observers.push(observer)
    } catch (error) {
      console.warn('Font loading monitoring setup failed:', error)
    }
  }

  private setupResourceMonitoring(): void {
    try {
      const observer = new PerformanceObserver(list => {
        const entries = list.getEntries()
        entries.forEach(entry => {
          if (entry.entryType === 'resource' && entry.name.includes('.css')) {
            this.processCSSLoadingEntry(entry as PerformanceResourceTiming)
          }
        })
      })

      observer.observe({ entryTypes: ['resource'] })
      this.observers.push(observer)
    } catch (error) {
      console.warn('Resource monitoring setup failed:', error)
    }
  }

  private setupMemoryMonitoring(): void {
    if ('memory' in performance) {
      setInterval(() => {
        this.checkMemoryUsage()
      }, 30000) // Check every 30 seconds
    }
  }

  private processFontLoadingEntry(entry: PerformanceResourceTiming): void {
    const fontFamily = this.extractFontFamily(entry.name)
    const loadTime = entry.responseEnd - entry.requestStart
    const size = entry.transferSize || entry.encodedBodySize || 0

    const metrics: FontLoadingMetrics = {
      fontFamily,
      loadTime,
      size,
      status: entry.responseEnd > 0 ? 'loaded' : 'error',
      timestamp: Date.now(),
    }

    this.fontMetrics.push(metrics)

    // Keep only last 50 entries
    if (this.fontMetrics.length > 50) {
      this.fontMetrics = this.fontMetrics.slice(-50)
    }

    // Check for performance alerts
    this.checkFontLoadingAlerts(metrics)
  }

  private processCSSLoadingEntry(entry: PerformanceResourceTiming): void {
    const loadTime = entry.responseEnd - entry.requestStart
    const size = entry.transferSize || entry.encodedBodySize || 0
    const compressionRatio =
      entry.encodedBodySize > 0
        ? (entry.decodedBodySize || entry.encodedBodySize) /
          entry.encodedBodySize
        : 1

    const metrics: CSSBundleMetrics = {
      totalSize: size,
      brandRelatedSize: this.estimateBrandRelatedSize(entry.name, size),
      compressionRatio,
      loadTime,
      cacheHitRate: this.calculateCacheHitRate(entry),
      timestamp: Date.now(),
    }

    this.cssMetrics.push(metrics)

    // Keep only last 20 entries
    if (this.cssMetrics.length > 20) {
      this.cssMetrics = this.cssMetrics.slice(-20)
    }

    // Check for performance alerts
    this.checkCSSBundleAlerts(metrics)
  }

  private extractFontFamily(url: string): string {
    // Extract font family from Google Fonts URL or file name
    if (url.includes('fonts.googleapis.com')) {
      const match = url.match(/family=([^&:]+)/)
      return match && match[1]
        ? decodeURIComponent(match[1].replace(/\+/g, ' '))
        : 'Unknown'
    }

    // Extract from file name
    const fileName = url.split('/').pop() || ''
    if (fileName.toLowerCase().includes('montserrat')) return 'Montserrat'
    if (fileName.toLowerCase().includes('source')) return 'Source Sans 3'

    return 'Unknown'
  }

  private estimateBrandRelatedSize(url: string, totalSize: number): number {
    // Estimate what portion of CSS is brand-related
    if (url.includes('brand') || url.includes('theme')) {
      return totalSize
    }

    // For main CSS bundles, estimate 20-30% is brand-related
    return Math.round(totalSize * 0.25)
  }

  private calculateCacheHitRate(entry: PerformanceResourceTiming): number {
    // If transfer size is 0 but encoded size > 0, it's likely a cache hit
    if (entry.transferSize === 0 && (entry.encodedBodySize || 0) > 0) {
      return 1.0 // 100% cache hit
    }

    // If transfer size is much smaller than encoded size, partial cache hit
    const encodedSize = entry.encodedBodySize || entry.transferSize || 0
    if (encodedSize > 0 && entry.transferSize < encodedSize * 0.1) {
      return 0.9 // 90% cache hit
    }

    return 0 // No cache hit
  }

  private checkFontLoadingAlerts(metrics: FontLoadingMetrics): void {
    const thresholds = {
      slow: 2000, // 2 seconds
      verySlow: 5000, // 5 seconds
      large: 100000, // 100KB
      veryLarge: 500000, // 500KB
    }

    if (metrics.loadTime > thresholds.verySlow) {
      this.createAlert(
        'font-load-slow',
        'critical',
        `Font ${metrics.fontFamily} took ${Math.round(metrics.loadTime)}ms to load`,
        metrics
      )
    } else if (metrics.loadTime > thresholds.slow) {
      this.createAlert(
        'font-load-slow',
        'high',
        `Font ${metrics.fontFamily} took ${Math.round(metrics.loadTime)}ms to load`,
        metrics
      )
    }

    if (metrics.size > thresholds.veryLarge) {
      this.createAlert(
        'font-load-slow',
        'high',
        `Font ${metrics.fontFamily} is ${Math.round(metrics.size / 1024)}KB`,
        metrics
      )
    }
  }

  private checkCSSBundleAlerts(metrics: CSSBundleMetrics): void {
    const thresholds = {
      large: 300000, // 300KB
      veryLarge: 500000, // 500KB
      slowLoad: 3000, // 3 seconds
    }

    if (metrics.totalSize > thresholds.veryLarge) {
      this.createAlert(
        'bundle-size-large',
        'critical',
        `CSS bundle is ${Math.round(metrics.totalSize / 1024)}KB`,
        metrics
      )
    } else if (metrics.totalSize > thresholds.large) {
      this.createAlert(
        'bundle-size-large',
        'high',
        `CSS bundle is ${Math.round(metrics.totalSize / 1024)}KB`,
        metrics
      )
    }

    if (metrics.loadTime > thresholds.slowLoad) {
      this.createAlert(
        'bundle-size-large',
        'medium',
        `CSS bundle took ${Math.round(metrics.loadTime)}ms to load`,
        metrics
      )
    }
  }

  private checkMemoryUsage(): void {
    if ('memory' in performance) {
      const perfMemory = performance as unknown as {
        memory?: {
          usedJSHeapSize?: number
          jsHeapSizeLimit?: number
        }
      }
      const memory = perfMemory.memory
      if (!memory) return

      const usedMB = (memory.usedJSHeapSize || 0) / 1024 / 1024
      const limitMB = (memory.jsHeapSizeLimit || 0) / 1024 / 1024

      const usagePercent = limitMB > 0 ? (usedMB / limitMB) * 100 : 0

      if (usagePercent > 90) {
        this.createAlert(
          'memory-high',
          'critical',
          `Memory usage is ${Math.round(usagePercent)}% (${Math.round(usedMB)}MB)`,
          { usedMB, limitMB, usagePercent }
        )
      } else if (usagePercent > 75) {
        this.createAlert(
          'memory-high',
          'high',
          `Memory usage is ${Math.round(usagePercent)}% (${Math.round(usedMB)}MB)`,
          { usedMB, limitMB, usagePercent }
        )
      }
    }
  }

  private createAlert(
    type: PerformanceAlert['type'],
    severity: PerformanceAlert['severity'],
    message: string,
    data:
      | FontLoadingMetrics
      | CSSBundleMetrics
      | ValidationPerformanceMetrics
      | Record<string, unknown>
  ): void {
    const alert: PerformanceAlert = {
      type,
      severity,
      message,
      data,
      timestamp: Date.now(),
    }

    this.alerts.push(alert)

    // Keep only last 100 alerts
    if (this.alerts.length > 100) {
      this.alerts = this.alerts.slice(-100)
    }

    // Log alert
    console.warn(`Performance Alert [${severity}]:`, message, data)

    // In production, send to monitoring service
    if (
      typeof window !== 'undefined' &&
      window.location.hostname !== 'localhost'
    ) {
      this.sendAlertToMonitoringService(alert)
    }
  }

  private sendAlertToMonitoringService(alert: PerformanceAlert): void {
    fetch('/api/monitoring/performance-alerts', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(alert),
    }).catch(error => {
      console.warn('Failed to send performance alert:', error)
    })
  }

  public measureValidationPerformance<T>(validationFn: () => T): T {
    const startTime = performance.now()
    const result = validationFn()
    const endTime = performance.now()

    const metrics: ValidationPerformanceMetrics = {
      totalValidationTime: endTime - startTime,
      rulesExecuted: 0, // Would be set by validation function
      elementsValidated: 0, // Would be set by validation function
      averageTimePerElement: 0,
      averageTimePerRule: 0,
      timestamp: Date.now(),
    }

    this.validationMetrics.push(metrics)

    // Keep only last 50 entries
    if (this.validationMetrics.length > 50) {
      this.validationMetrics = this.validationMetrics.slice(-50)
    }

    // Check for validation performance alerts
    if (metrics.totalValidationTime > 100) {
      // 100ms threshold
      this.createAlert(
        'validation-slow',
        'medium',
        `Validation took ${Math.round(metrics.totalValidationTime)}ms`,
        metrics
      )
    }

    return result
  }

  public getFontLoadingMetrics(): FontLoadingMetrics[] {
    return [...this.fontMetrics]
  }

  public getCSSBundleMetrics(): CSSBundleMetrics[] {
    return [...this.cssMetrics]
  }

  public getValidationMetrics(): ValidationPerformanceMetrics[] {
    return [...this.validationMetrics]
  }

  public getPerformanceAlerts(): PerformanceAlert[] {
    return [...this.alerts]
  }

  public getRecentAlerts(minutes: number = 60): PerformanceAlert[] {
    const cutoff = Date.now() - minutes * 60 * 1000
    return this.alerts.filter(alert => alert.timestamp >= cutoff)
  }

  public getPerformanceSummary(): {
    fonts: { averageLoadTime: number; totalSize: number; count: number }
    css: { averageLoadTime: number; totalSize: number; count: number }
    validation: { averageTime: number; count: number }
    alerts: {
      total: number
      byType: Record<string, number>
      bySeverity: Record<string, number>
    }
  } {
    const fontSummary = this.fontMetrics.reduce(
      (acc, metric) => ({
        totalLoadTime: acc.totalLoadTime + metric.loadTime,
        totalSize: acc.totalSize + metric.size,
        count: acc.count + 1,
      }),
      { totalLoadTime: 0, totalSize: 0, count: 0 }
    )

    const cssSummary = this.cssMetrics.reduce(
      (acc, metric) => ({
        totalLoadTime: acc.totalLoadTime + metric.loadTime,
        totalSize: acc.totalSize + metric.totalSize,
        count: acc.count + 1,
      }),
      { totalLoadTime: 0, totalSize: 0, count: 0 }
    )

    const validationSummary = this.validationMetrics.reduce(
      (acc, metric) => ({
        totalTime: acc.totalTime + metric.totalValidationTime,
        count: acc.count + 1,
      }),
      { totalTime: 0, count: 0 }
    )

    const alertsByType: Record<string, number> = {}
    const alertsBySeverity: Record<string, number> = {}

    this.alerts.forEach(alert => {
      alertsByType[alert.type] = (alertsByType[alert.type] || 0) + 1
      alertsBySeverity[alert.severity] =
        (alertsBySeverity[alert.severity] || 0) + 1
    })

    return {
      fonts: {
        averageLoadTime:
          fontSummary.count > 0
            ? fontSummary.totalLoadTime / fontSummary.count
            : 0,
        totalSize: fontSummary.totalSize,
        count: fontSummary.count,
      },
      css: {
        averageLoadTime:
          cssSummary.count > 0
            ? cssSummary.totalLoadTime / cssSummary.count
            : 0,
        totalSize: cssSummary.totalSize,
        count: cssSummary.count,
      },
      validation: {
        averageTime:
          validationSummary.count > 0
            ? validationSummary.totalTime / validationSummary.count
            : 0,
        count: validationSummary.count,
      },
      alerts: {
        total: this.alerts.length,
        byType: alertsByType,
        bySeverity: alertsBySeverity,
      },
    }
  }

  public clearMetrics(): void {
    this.fontMetrics = []
    this.cssMetrics = []
    this.validationMetrics = []
    this.alerts = []
  }

  public destroy(): void {
    this.observers.forEach(observer => observer.disconnect())
    this.observers = []
    this.clearMetrics()
  }
}

// Global performance monitoring service
export const performanceMonitoringService = new PerformanceMonitoringService()

// Export convenience functions
export function measureValidationPerformance<T>(validationFn: () => T): T {
  return performanceMonitoringService.measureValidationPerformance(validationFn)
}

export function getFontLoadingMetrics(): FontLoadingMetrics[] {
  return performanceMonitoringService.getFontLoadingMetrics()
}

export function getCSSBundleMetrics(): CSSBundleMetrics[] {
  return performanceMonitoringService.getCSSBundleMetrics()
}

export function getValidationMetrics(): ValidationPerformanceMetrics[] {
  return performanceMonitoringService.getValidationMetrics()
}

export function getPerformanceAlerts(): PerformanceAlert[] {
  return performanceMonitoringService.getPerformanceAlerts()
}

export function getPerformanceSummary() {
  return performanceMonitoringService.getPerformanceSummary()
}
